// MIX Admin Panel Service Worker
const CACHE_NAME = 'mix-admin-v1.0.0';
const urlsToCache = [
  '/admin/',
  '/admin/dashboard',
  '/admin/users',
  '/admin/matches', 
  '/admin/messages',
  '/admin/subscriptions',
  '/admin/reports',
  '/admin/analytics',
  '/admin/settings',
  '/admin/payments',
  '/admin/verifications',
  '/admin/notifications',
  '/admin-manifest.json',
  '/admin-icon-192x192.png',
  '/admin-icon-512x512.png'
];

// Install event - cache admin resources
self.addEventListener('install', event => {
  console.log('üîß MIX Admin SW: Installing service worker');
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        console.log('üì¶ MIX Admin SW: Caching admin panel resources');
        return cache.addAll(urlsToCache);
      })
      .catch(err => {
        console.error('‚ùå MIX Admin SW: Cache install failed:', err);
      })
  );
  self.skipWaiting();
});

// Activate event - clean up old caches
self.addEventListener('activate', event => {
  console.log('‚úÖ MIX Admin SW: Activating service worker');
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheName !== CACHE_NAME && cacheName.startsWith('mix-admin-')) {
            console.log('üóëÔ∏è MIX Admin SW: Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  self.clients.claim();
});

// Fetch event - serve from cache with network fallback
self.addEventListener('fetch', event => {
  // Only handle admin routes
  if (!event.request.url.includes('/admin')) {
    return;
  }

  event.respondWith(
    caches.match(event.request)
      .then(response => {
        // Return cached version or fetch from network
        if (response) {
          console.log('üì¶ MIX Admin SW: Serving from cache:', event.request.url);
          return response;
        }

        console.log('üåê MIX Admin SW: Fetching from network:', event.request.url);
        return fetch(event.request).then(fetchResponse => {
          // Don't cache non-successful responses
          if (!fetchResponse || fetchResponse.status !== 200 || fetchResponse.type !== 'basic') {
            return fetchResponse;
          }

          // Clone the response for caching
          const responseToCache = fetchResponse.clone();
          caches.open(CACHE_NAME)
            .then(cache => {
              // Only cache admin routes
              if (event.request.url.includes('/admin')) {
                cache.put(event.request, responseToCache);
              }
            });

          return fetchResponse;
        }).catch(() => {
          // Return offline page for admin routes
          if (event.request.url.includes('/admin')) {
            return caches.match('/admin-offline.html');
          }
        });
      })
  );
});

// Background sync for admin actions
self.addEventListener('sync', event => {
  console.log('üîÑ MIX Admin SW: Background sync triggered:', event.tag);

  if (event.tag === 'admin-action-sync') {
    event.waitUntil(syncAdminActions());
  }
});

// Push notifications for admin alerts
self.addEventListener('push', event => {
  console.log('üîî MIX Admin SW: Push notification received');

  const options = {
    body: event.data ? event.data.text() : 'Nova atividade no painel administrativo',
    icon: '/admin-icon-192x192.png',
    badge: '/admin-icon-192x192.png',
    vibrate: [100, 50, 100],
    data: {
      dateOfArrival: Date.now(),
      primaryKey: 1
    },
    actions: [
      {
        action: 'explore',
        title: 'Ver Detalhes',
        icon: '/admin-icon-192x192.png'
      },
      {
        action: 'close', 
        title: 'Fechar'
      }
    ]
  };

  event.waitUntil(
    self.registration.showNotification('MIX Admin', options)
  );
});

// Handle notification clicks
self.addEventListener('notificationclick', event => {
  console.log('üîî MIX Admin SW: Notification clicked:', event.action);

  event.notification.close();

  if (event.action === 'explore') {
    event.waitUntil(
      clients.openWindow('/admin/dashboard')
    );
  }
});

// Sync admin actions when back online
async function syncAdminActions() {
  try {
    console.log('üîÑ MIX Admin SW: Syncing admin actions...');
    // Implement admin action sync logic here
    return Promise.resolve();
  } catch (error) {
    console.error('‚ùå MIX Admin SW: Sync failed:', error);
    throw error;
  }
}

console.log('üöÄ MIX Admin Service Worker loaded and ready!');

