import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, ArrowLeft } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import mixLogo from "@assets/Logo_MIX_horizontal_1752607947932.png";

export default function PhoneAuth() {
  const [, setLocation] = useLocation();
  // ‚úÖ Detectar modo pela URL - se veio de /login, inicia em modo login
  const urlParams = new URLSearchParams(window.location.search);
  const initialMode = urlParams.get('mode') === 'register' ? 'register' : 'login';
  const [mode, setMode] = useState<"login" | "register">(initialMode);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const formatPhone = (value: string) => {
    const numbers = value.replace(/\D/g, '');
    if (numbers.length <= 2) return numbers;
    if (numbers.length <= 7) return `(${numbers.slice(0, 2)}) ${numbers.slice(2)}`;
    return `(${numbers.slice(0, 2)}) ${numbers.slice(2, 7)}-${numbers.slice(7, 11)}`;
  };

  const handlePhoneChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPhone(formatPhone(e.target.value));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    // Valida√ß√£o de telefone
    const phoneRegex = /^\(\d{2}\)\s\d{4,5}-\d{4}$/;
    if (!phone || !phoneRegex.test(phone)) {
      setError("Digite um telefone v√°lido: (11) 99999-9999");
      setIsLoading(false);
      return;
    }

    if (!password || password.length < 6) {
      setError("A senha deve ter pelo menos 6 caracteres");
      setIsLoading(false);
      return;
    }

    try {
      const cleanPhone = phone.replace(/\D/g, '');

      if (mode === "register") {
        // CADASTRO
        if (!email.trim()) {
          setError("Email √© obrigat√≥rio");
          setIsLoading(false);
          return;
        }

        if (!email.includes('@')) {
          setError("Digite um email v√°lido");
          setIsLoading(false);
          return;
        }

        if (password !== confirmPassword) {
          setError("As senhas n√£o coincidem");
          setIsLoading(false);
          return;
        }

        const username = email.split('@')[0].toLowerCase() + Math.random().toString(36).substring(2, 5);

        console.log("üîµ Iniciando cadastro:", { email, phone: cleanPhone });

        const response = await fetch('/api/auth/phone/register', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            username,
            email: email.trim().toLowerCase(),
            phone: cleanPhone,
            password
          }),
          credentials: 'include'
        });

        console.log("üîµ Response status:", response.status, response.ok);

        const data = await response.json();
        console.log("üîµ Response data:", data);

        if (!response.ok) {
          console.error("‚ùå Erro no cadastro:", data.message);
          setError(data.message || 'Erro ao criar conta');
          setIsLoading(false);
          return;
        }

        console.log("‚úÖ Cadastro realizado com sucesso!");

        toast({
          title: "Conta criada com sucesso!",
          description: "Redirecionando para completar seu perfil...",
        });

        setTimeout(() => {
          console.log("üîµ Redirecionando para /onboarding-flow");
          window.location.href = "/onboarding-flow";
        }, 500);

      } else {
        // LOGIN
        const response = await fetch('/api/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            identifier: cleanPhone,
            password
          }),
          credentials: 'include'
        });

        const data = await response.json();

        if (!response.ok) {
          // Se usu√°rio n√£o existe, redirecionar para p√°gina de boas-vindas
          if (data.userNotFound) {
            setLocation("/welcome-new-user");
            return;
          }

          setError(data.message || 'Telefone ou senha incorretos');
          setIsLoading(false);
          return;
        }

        toast({
          title: "Login realizado!",
          description: "Bem-vindo de volta!",
        });

        // Buscar dados do usu√°rio para verificar se perfil est√° completo
        const userResponse = await fetch('/api/auth/user', {
          credentials: 'include'
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();

          setTimeout(() => {
            if (userData.isProfileComplete) {
              window.location.href = "/discover";
            } else {
              window.location.href = "/onboarding-flow";
            }
          }, 500);
        } else {
          // Fallback: redirecionar para discover
          setTimeout(() => {
            window.location.href = "/discover";
          }, 500);
        }
      }

    } catch (err) {
      setError("Erro ao conectar com o servidor. Verifique sua conex√£o.");
      setIsLoading(false);
    }
  };

  const toggleMode = () => {
    setMode(mode === "login" ? "register" : "login");
    setError("");
    setEmail("");
    setPassword("");
    setConfirmPassword("");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex flex-col items-center justify-center p-6">
      <div className="w-full max-w-md">
        <button
          onClick={() => setLocation(mode === "register" ? '/register' : '/login')}
          className="text-white/90 hover:text-white flex items-center gap-2 mb-8 transition-colors"
          data-testid="button-back"
        >
          <ArrowLeft className="h-5 w-5" />
          <span className="text-sm font-medium">Voltar</span>
        </button>

        <div className="flex justify-center mb-8">
          <img src={mixLogo} alt="MIX" className="h-16 w-auto" data-testid="img-logo" />
        </div>

        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-white mb-2" data-testid="text-title">
            {mode === "register" ? "Cadastre-se com Celular" : "Entrar com Celular"}
          </h1>
          <p className="text-white/70 text-sm" data-testid="text-subtitle">
            {mode === "register" 
              ? "Preencha seus dados para criar sua conta" 
              : "Digite seu telefone e senha para entrar"}
          </p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          {error && (
            <Alert className="bg-red-500/20 border-red-500/50">
              <AlertCircle className="h-4 w-4 text-white" />
              <AlertDescription className="text-white">{error}</AlertDescription>
            </Alert>
          )}

          <div className="space-y-4">
            {mode === "register" && (
              <div>
                <label className="block text-white/90 mb-2 ml-4 font-medium text-sm">
                  Email
                </label>
                <Input
                  type="email"
                  placeholder="seu@email.com"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="h-14 text-base bg-white/10 border-2 border-white/20 focus:border-pink-500/50 rounded-full text-white placeholder:text-white/50 px-6"
                  disabled={isLoading}
                  data-testid="input-email"
                />
              </div>
            )}

            <div>
              <label className="block text-white/90 mb-2 ml-4 font-medium text-sm">
                N√∫mero de Celular
              </label>
              <Input
                type="tel"
                placeholder="(11) 99999-9999"
                value={phone}
                onChange={handlePhoneChange}
                maxLength={15}
                className="h-14 text-base bg-white/10 border-2 border-white/20 focus:border-pink-500/50 rounded-full text-white placeholder:text-white/50 px-6"
                disabled={isLoading}
                data-testid="input-phone"
              />
            </div>

            <div>
