import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, Smartphone } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import mixLogoHorizontal from "@assets/Logo_MIX_horizontal_1750591494976.png";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { SiFacebook } from "react-icons/si";
import { FaGoogle } from "react-icons/fa";

function RegisterContent() {
  const [, setLocation] = useLocation();
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleGoogleSuccess = async (credentialResponse: any) => {
    try {
      setIsLoading(true);
      setError("");

      console.log("üü¢ Enviando credencial do Google para o backend...");

      const response = await fetch("/api/auth/google", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: 'include',
        body: JSON.stringify({ 
          credential: credentialResponse.credential
        }),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        console.log("‚úÖ Cadastro realizado com sucesso:", data);

        toast({ 
          title: "Conta criada!", 
          description: "Bem-vindo ao MIX!" 
        });

        await new Promise(resolve => setTimeout(resolve, 500));

        // Always redirect to onboarding for registration
        console.log("‚ö†Ô∏è Redirecionando para /onboarding-flow");
        window.location.href = '/onboarding-flow';
      } else {
        console.error("‚ùå Erro no cadastro:", data);
        setError(data.message || "Erro ao criar conta com Google. Tente novamente.");
      }
    } catch (err: any) {
      console.error("‚ùå Erro de conex√£o:", err);
      setError("Erro de conex√£o com o servidor. Verifique sua internet e tente novamente.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleError = () => {
    console.error("‚ùå Erro no Google Register");

    const currentDomain = window.location.origin;
    const isDomainError = true;

    if (isDomainError) {
      setError(
        `‚ö†Ô∏è CONFIGURA√á√ÉO NECESS√ÅRIA:\n\n` +
        `O dom√≠nio ${currentDomain} precisa ser autorizado no Google Cloud Console.\n\n` +
        `Por favor, adicione este dom√≠nio em "Origens JavaScript autorizadas":\n` +
        `console.cloud.google.com/apis/credentials`
      );
    } else {
      setError("Erro ao autenticar com Google. Por favor, tente novamente.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex flex-col items-center justify-between p-8 py-16">
      <div className="w-full max-w-md flex-1 flex flex-col justify-center">
        <div className="text-center mb-12">
          <img 
            src={mixLogoHorizontal} 
            alt="MIX" 
            className="h-20 w-auto mx-auto drop-shadow-lg"
            data-testid="img-logo"
          />
        </div>

        <div className="text-center mb-8">
          <h1 className="text-white text-4xl font-bold mb-3">Criar Conta</h1>
          <p className="text-white/90 text-lg">Escolha como deseja se cadastrar</p>
        </div>

        {error && (
          <Alert variant="destructive" className="mb-6 bg-white/20 border-white/30 text-white">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        <div className="space-y-4">
          <div className="relative">
            <div 
              className="opacity-0 absolute inset-0 w-full h-14 cursor-pointer"
              style={{ zIndex: 10 }}
            >
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={handleGoogleError}
                size="large"
                text="signup_with"
                shape="pill"
              />
            </div>

            <div
              className="w-full h-14 bg-white hover:bg-gray-50 text-gray-900 font-semibold text-lg rounded-full shadow-lg border-0 flex items-center justify-center gap-3 pointer-events-none transition-colors"
              data-testid="button-google-register"
            >
              <FaGoogle className="w-6 h-6 text-blue-500" />
              Continuar com o Google
            </div>
          </div>

          <Button
            onClick={() => setLocation("/phone-auth")}
            className="w-full h-14 bg-white hover:bg-gray-50 text-gray-900 font-semibold text-lg rounded-full shadow-lg border-0 flex items-center justify-center gap-3"
            data-testid="button-phone-register"
          >
            <Smartphone className="w-6 h-6 text-gray-900" />
            Continuar com n√∫mero de telefone
          </Button>

          <Button
            onClick={() => {
              toast({ 
                title: "Em breve", 
                description: "Login com Facebook estar√° dispon√≠vel em breve" 
              });
            }}
            className="w-full h-14 bg-white hover:bg-gray-50 text-gray-900 font-semibold text-lg rounded-full shadow-lg border-0 flex items-center justify-center gap-3"
            data-testid="button-facebook-register"
          >
            <SiFacebook className="w-6 h-6 text-blue-600" />
            Continuar com o Facebook
          </Button>

          {isLoading && (
            <div className="text-white text-center mt-4 font-medium">Cadastrando...</div>
          )}
        </div>

        <div className="text-center mt-8">
          <p className="text-white text-base">
            J√° tem uma conta?{" "}
            <button
              onClick={() => setLocation("/login")}
              className="font-bold hover:underline"
              data-testid="link-login"
            >
              Entrar
            </button>
          </p>
        </div>
      </div>

      <div className="w-full max-w-md mt-8">
        <p className="text-white/90 text-xs text-center leading-relaxed">
          Ao continuar, voc√™ concorda com nossos{" "}
          <button
            onClick={() => setLocation("/terms")}
            className="underline font-medium"
            data-testid="link-terms"
          >
            Termos de Uso
          </button>
        </p>
      </div>
    </div>
  );
}

export default function Register() {
  const googleClientId = import.meta.env.VITE_GOOGLE_CLIENT_ID || '853566020139-jqljs5sf7didb7tc35shj73s8snldhdr.apps.googleusercontent.com';

  return (
    <GoogleOAuthProvider clientId={googleClientId}>
      <RegisterContent />
    </GoogleOAuthProvider>
  );
}

