import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowLeft, Copy, Check, Clock, CreditCard } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import mixLogo from "@assets/Logo_MIX_horizontal_1750591494976.png";

interface PaymentProps {
  plan?: string;
  amount?: number;
}

export default function Payment() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [selectedPayment, setSelectedPayment] = useState<'pix' | 'card' | null>(null);
  const [pixCode, setPixCode] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [copied, setCopied] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState<'pending' | 'processing' | 'success' | 'failed'>('pending');
  const [countdown, setCountdown] = useState(600); // 10 minutos

  // Simular dados do plano (em produ√ß√£o viriam da navega√ß√£o)
  const planData = {
    name: "Mix Premium",
    price: 29.90,
    features: ["Likes ilimitados", "Ver quem curtiu voc√™", "Boost mensal"]
  };

  // Gerar PIX Code e QR Code autom√°tico
  useEffect(() => {
    if (selectedPayment === 'pix') {
      generatePixPayment();
    }
  }, [selectedPayment]);

  // Countdown timer
  useEffect(() => {
    if (selectedPayment === 'pix' && countdown > 0) {
      const timer = setInterval(() => {
        setCountdown(prev => prev - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [selectedPayment, countdown]);

  const generatePixPayment = async () => {
    try {
      // Simular gera√ß√£o de PIX (em produ√ß√£o seria uma API real)
      const pixKey = "mix.dating@example.com"; // Chave teste
      const amount = planData.price;
      const merchantName = "MIX APP DIGITAL";
      const city = "SAO PAULO";
      const txId = `MIXAPP${Date.now()}`;

      // Gerar PIX Code simplificado (em produ√ß√£o usar biblioteca PIX adequada)
      const pixPayload = generatePixPayload(pixKey, amount, merchantName, city, txId);
      setPixCode(pixPayload);

      // Gerar QR Code usando API p√∫blica
      const qrUrl = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(pixPayload)}`;
      setQrCodeUrl(qrUrl);

      // Simular monitoramento de pagamento
      simulatePaymentMonitoring();

    } catch (error) {
      console.error('Erro ao gerar PIX:', error);
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel gerar o PIX. Tente novamente.",
        variant: "destructive"
      });
    }
  };

  const generatePixPayload = (pixKey: string, amount: number, merchantName: string, city: string, txId: string) => {
    // Formato PIX simplificado para teste
    return `00020126580014br.gov.bcb.pix0136${pixKey}52040000530398654${amount.toFixed(2)}5802BR5913${merchantName}6009${city}62070503${txId}6304`;
  };

  const simulatePaymentMonitoring = () => {
    // Simular aprova√ß√£o do pagamento ap√≥s 30 segundos
    setTimeout(() => {
      setPaymentStatus('processing');
      toast({
        title: "Pagamento detectado!",
        description: "Processando seu pagamento...",
      });

      setTimeout(() => {
        setPaymentStatus('success');
        toast({
          title: "Pagamento aprovado!",
          description: "Bem-vindo ao Mix Premium!",
        });

        // Redirecionar ap√≥s 2 segundos
        setTimeout(() => {
          setLocation("/discover");
        }, 2000);
      }, 3000);
    }, 30000);
  };

  const copyPixCode = () => {
    navigator.clipboard.writeText(pixCode);
    setCopied(true);
    toast({
      title: "PIX copiado!",
      description: "Cole no seu app do banco para pagar",
    });
    setTimeout(() => setCopied(false), 2000);
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  if (paymentStatus === 'success') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-500 via-purple-500 to-blue-500 flex items-center justify-center p-4">
        <Card className="w-full max-w-md text-center">
          <CardHeader>
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center">
                <Check className="w-8 h-8 text-green-600" />
              </div>
            </div>
            <CardTitle className="text-2xl text-green-600">Pagamento Aprovado!</CardTitle>
            <CardDescription>Bem-vindo ao Mix Premium</CardDescription>
          </CardHeader>
          <CardContent>
            <p className="text-gray-600 mb-4">
              Agora voc√™ tem acesso a todas as funcionalidades premium do MIX!
            </p>
            <Button 
              onClick={() => setLocation("/discover")}
              className="w-full bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700"
            >
              Come√ßar a usar Premium
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-500 via-purple-500 to-blue-500 flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-white/10 backdrop-blur-sm">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => setLocation("/seu-mix")}
          className="text-white hover:bg-white/20"
        >
          <ArrowLeft className="w-6 h-6" />
        </Button>
        <img src={mixLogo} alt="MIX" className="h-8 w-auto" />
        <div className="w-10" />
      </div>

      <div className="flex-1 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl">Finalizar Pagamento</CardTitle>
            <CardDescription>
              {planData.name} - R$ {planData.price.toFixed(2)}/m√™s
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-4">
            {!selectedPayment && (
              <div className="space-y-3">
                <h3 className="font-semibold text-center">Escolha a forma de pagamento:</h3>

                <Button
                  variant="outline"
                  className="w-full h-16 flex items-center justify-center space-x-3"
                  onClick={() => setSelectedPayment('pix')}
                >
                  <div className="w-8 h-8 bg-green-500 rounded flex items-center justify-center">
                    <span className="text-white font-bold text-xs">PIX</span>
                  </div>
                  <div className="text-left">
                    <div className="font-semibold">PIX</div>
                    <div className="text-sm text-gray-500">Pagamento instant√¢neo</div>
                  </div>
                </Button>

                <Button
                  variant="outline"
                  className="w-full h-16 flex items-center justify-center space-x-3"
                  onClick={() => setSelectedPayment('card')}
                >
                  <CreditCard className="w-8 h-8 text-blue-500" />
                  <div className="text-left">
                    <div className="font-semibold">Cart√£o de Cr√©dito</div>
                    <div className="text-sm text-gray-500">Em breve</div>
                  </div>
                </Button>
              </div>
            )}

            {selectedPayment === 'pix' && (
              <div className="space-y-4">
                <div className="text-center">
                  <div className="flex items-center justify-center space-x-2 mb-2">
                    <Clock className="w-4 h-4 text-orange-500" />
                    <span className="text-sm font-medium">
                      Expira em: {formatTime(countdown)}
                    </span>
                  </div>

                  {qrCodeUrl && (
                    <div className="flex justify-center mb-4">
                      <img 
                        src={qrCodeUrl} 
                        alt="QR Code PIX" 
                        className="border rounded-lg"
                      />
                    </div>
                  )}

                  <p className="text-sm text-gray-600 mb-3">
                    Escaneie o QR Code ou copie o c√≥digo PIX:
                  </p>

                  <div className="bg-gray-50 p-3 rounded-lg">
                    <div className="text-xs font-mono break-all mb-2">
                      {pixCode}
                    </div>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={copyPixCode}
                      className="w-full"
                      disabled={copied}
                    >
                      {copied ? (
                        <>
                          <Check className="w-4 h-4 mr-2" />
                          Copiado!
                        </>
                      ) : (
                        <>
                          <Copy className="w-4 h-4 mr-2" />
                          Copiar c√≥digo PIX
                        </>
                      )}
                    </Button>
                  </div>

                  {paymentStatus === 'processing' && (
                    <div className="mt-4 p-3 bg-blue-50 rounded-lg">
                      <div className="flex items-center justify-center space-x-2">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-500"></div>
                        <span className="text-sm text-blue-600">Processando pagamento...</span>
                      </div>
                    </div>
                  )}
                </div>

                <Button
                  variant="ghost"
                  onClick={() => setSelectedPayment(null)}
                  className="w-full"
                >
                  Voltar
                </Button>
              </div>
            )}

            {selectedPayment === 'card' && (
              <div className="text-center space-y-4">
                <div className="p-8 bg-gray-50 rounded-lg">
                  <CreditCard className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="font-semibold text-gray-600">Em breve</h3>
                  <p className="text-sm text-gray-500">
                    Pagamento por cart√£o ser√° disponibilizado em breve
                  </p>
                </div>

                <Button
                  variant="ghost"
                  onClick={() => setSelectedPayment(null)}
                  className="w-full"
                >
                  Voltar
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/admin-subscriptio.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import AdminLayout from "@/components/admin/AdminLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { 
  CreditCard, 
  Search, 
  Filter, 
  Crown,
  Star,
  Calendar,
  DollarSign,
  Eye,
  Ban,
  CheckCircle
} from "lucide-react";

interface Subscription {
  id: number;
  userId: number;
  userName: string;
  planType: string;
  status: string;
  startDate: string;
  endDate: string;
  amount: number;
  stripeSubscriptionId: string;
  isActive: boolean;
}

export default function AdminSubscriptionsNew() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  useEffect(() => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
      setLocation("/admin/login");
    }
  }, [setLocation]);

  const { data: subscriptions = [], isLoading } = useQuery({
    queryKey: ['/api/admin/subscriptions', searchTerm, statusFilter],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.set('search', searchTerm);
      if (statusFilter !== 'all') params.set('status', statusFilter);

      const response = await fetch(`/api/admin/subscriptions?${params}`);
      if (!response.ok) throw new Error('Failed to fetch subscriptions');
      return response.json();
    }
  });

  const cancelSubscriptionMutation = useMutation({
    mutationFn: async (subscriptionId: number) => {
      const response = await fetch(`/api/admin/subscriptions/${subscriptionId}/cancel`, {
        method: 'POST'
      });
      if (!response.ok) throw new Error('Failed to cancel subscription');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/subscriptions'] });
      toast({
        title: "‚úÖ Assinatura Cancelada",
        description: "Assinatura foi cancelada com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "‚ùå Erro",
        description: "Falha ao cancelar assinatura",
        variant: "destructive"
      });
    }
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    }).format(amount / 100); // Convert from cents
  };

  const getPlanIcon = (planType: string) => {
    switch (planType.toLowerCase()) {
      case 'premium':
        return <Crown className="w-4 h-4 text-yellow-400" />;
      case 'vip':
        return <Star className="w-4 h-4 text-purple-400" />;
      default:
        return <CreditCard className="w-4 h-4 text-blue-400" />;
    }
  };

  if (isLoading) {
    return (
      <AdminLayout title="Gerenciar Assinaturas">
        <div className="flex items-center justify-center h-64 w-full">
          <div className="animate-spin w-8 h-8 border-4 border-pink-500 border-t-transparent rounded-full"></div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout title="Gerenciar Assinaturas">
      <div className="space-y-3 w-full max-w-full overflow-x-hidden">
        {/* Search and Filters */}
        <Card className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 w-full">
          <div className="flex flex-col sm:flex-row gap-3 w-full">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-blue-300" />
              <Input
                placeholder="Buscar assinaturas..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 bg-blue-700/50 border-blue-600/50 text-white text-sm w-full"
              />
            </div>
            <div className="flex gap-2">
              <Button
                variant={statusFilter === 'all' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('all')}
                className="text-xs whitespace-nowrap"
              >
                Todas
              </Button>
              <Button
                variant={statusFilter === 'active' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('active')}
                className="text-xs whitespace-nowrap"
              >
                Ativas
              </Button>
              <Button
                variant={statusFilter === 'cancelled' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('cancelled')}
                className="text-xs whitespace-nowrap"
              >
                Canceladas
              </Button>
            </div>
          </div>
        </Card>

        {/* Subscriptions Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3 w-full">
          {subscriptions.map((subscription: Subscription) => (
            <Card key={subscription.id} className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 hover:bg-blue-800/70 transition-all w-full">
              {/* Subscription Header */}
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    {getPlanIcon(subscription.planType)}
                    <h3 className="text-sm font-semibold text-white truncate">
                      {subscription.userName}
                    </h3>
                  </div>
                  <p className="text-xs text-blue-200">Plano {subscription.planType}</p>
                </div>
                <div className="flex flex-col items-end gap-1">
                  <Badge 
                    variant={subscription.isActive ? "default" : "secondary"} 
                    className={`text-xs ${subscription.isActive ? 'bg-green-500/20 text-green-300' : 'bg-red-500/20 text-red-300'}`}
                  >
                    {subscription.isActive ? 'Ativa' : 'Cancelada'}
                  </Badge>
                  <span className="text-xs text-blue-300 font-semibold">
                    {formatCurrency(subscription.amount)}
                  </span>
                </div>
              </div>

              {/* Subscription Details */}
              <div className="space-y-2 mb-3">
                <div className="flex items-center justify-between text-xs">
                  <span className="text-blue-200">In√≠cio:</span>
                  <span className="text-white">{formatDate(subscription.startDate)}</span>
                </div>
                <div className="flex items-center justify-between text-xs">
                  <span className="text-blue-200">Vencimento:</span>
                  <span className="text-white">{formatDate(subscription.endDate)}</span>
                </div>
                <div className="flex items-center justify-between text-xs">
                  <span className="text-blue-200">Stripe ID:</span>
                  <span className="text-white font-mono text-xs truncate max-w-24">
                    {subscription.stripeSubscriptionId}
                  </span>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex gap-2">
                <Button
                  size="sm"
                  onClick={() => setLocation(`/admin/subscription-details/${subscription.id}`)}
                  className="flex-1 bg-gradient-to-r from-pink-500 to-purple-500 hover:from-pink-600 hover:to-purple-600 text-white text-xs"
                >
                  <Eye className="w-3 h-3 mr-1" />
                  Ver
                </Button>
                {subscription.isActive && (
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => {
                      if (confirm('Tem certeza que deseja cancelar esta assinatura?')) {
                        cancelSubscriptionMutation.mutate(subscription.id);
                      }
                    }}
                    className="border-red-500/50 text-red-400 hover:bg-red-500/20 text-xs"
                  >
                    <Ban className="w-3 h-3" />
                  </Button>
                )}
              </div>
            </Card>
          ))}
        </div>

        {/* Empty State */}
        {subscriptions.length === 0 && (
          <Card className="p-8 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 text-center w-full">
            <CreditCard className="w-12 h-12 text-blue-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-white mb-2">Nenhuma assinatura encontrada</h3>
            <p className="text-blue-200">Ajuste os filtros ou tente uma busca diferente.</p>
          </Card>
