import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, Smartphone } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import mixLogoHorizontal from "@assets/Logo_MIX_horizontal_1750591494976.png";
import { GoogleOAuthProvider, GoogleLogin } from '@react-oauth/google';
import { SiFacebook } from "react-icons/si";
import { FaGoogle } from "react-icons/fa";

function LoginContent() {
  const [, setLocation] = useLocation();
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleGoogleSuccess = async (credentialResponse: any) => {
    try {
      setIsLoading(true);
      setError("");

      console.log("üü¢ Enviando credencial do Google para o backend...");

      const response = await fetch("/api/auth/google", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: 'include',
        body: JSON.stringify({ 
          credential: credentialResponse.credential
        }),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        console.log("‚úÖ Login realizado com sucesso:", data);

        toast({ 
          title: "Login realizado!", 
          description: "Bem-vindo ao MIX!" 
        });

        await new Promise(resolve => setTimeout(resolve, 500));

        if (data.isProfileComplete) {
          console.log("‚úÖ Perfil completo - redirecionando para /discover");
          window.location.href = '/discover';
        } else {
          console.log("‚ö†Ô∏è Perfil incompleto - redirecionando para /onboarding-flow");
          window.location.href = '/onboarding-flow';
        }
      } else {
        console.error("‚ùå Erro no login:", data);
        setError(data.message || "Erro ao fazer login com Google. Tente novamente.");
      }
    } catch (err: any) {
      console.error("‚ùå Erro de conex√£o:", err);
      setError("Erro de conex√£o com o servidor. Verifique sua internet e tente novamente.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleError = () => {
    console.error("‚ùå Erro no Google Login");

    const currentDomain = window.location.origin;
    const isDomainError = true;

    if (isDomainError) {
      setError(
        `‚ö†Ô∏è CONFIGURA√á√ÉO NECESS√ÅRIA:\n\n` +
