import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { WebSocketManager } from "./websocket";
import { registerAdminRoutes } from "./admin-routes";
import { createStripeProducts } from "./stripe-setup";
import { insertUserSchema, registerUserSchema, insertProfileSchema, insertSwipeSchema, insertMessageSchema, type Swipe, type Match } from "@shared/schema";
import { z } from "zod";
import bcrypt from "bcrypt";
import passport from "passport";
import { setupAuth, requireAuth } from "./auth";
import { OAuthStorage } from "./oauth-storage";
import Stripe from "stripe";
import { OAuth2Client } from 'google-auth-library';

// Initialize Stripe (optional for development)
const stripe = process.env.STRIPE_SECRET_KEY
  ? new Stripe(process.env.STRIPE_SECRET_KEY, {
      apiVersion: "2025-06-30.basil",
    })
  : null;

function calculateAge(birthDate: string): number {
  const birth = new Date(birthDate);
  const today = new Date();
  let age = today.getFullYear() - birth.getFullYear();
  const monthDiff = today.getMonth() - birth.getMonth();

  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
    age--;
  }

  return age;
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Create HTTP server first
  const httpServer = createServer(app);

  // Initialize WebSocket manager early so routes can use it
  const wsManager = new WebSocketManager(httpServer);
  console.log('üîó WebSocket server initialized');

  // Setup authentication with OAuth
  setupAuth(app);

  // Initialize Google OAuth2 Client
  const googleClientId = process.env.GOOGLE_CLIENT_ID || '853566020139-jqljs5sf7didb7tc35shj73s8snldhdr.apps.googleusercontent.com';
  const googleClient = new OAuth2Client(googleClientId);

  // Google OAuth Login/Register Endpoint
  app.post("/api/auth/google", async (req, res) => {
    try {
      const { credential } = req.body;

      if (!credential) {
        return res.status(400).json({ 
          success: false, 
          message: "Credencial do Google n√£o fornecida" 
        });
      }

      console.log("‚úÖ Google OAuth: Validando credencial JWT...");

      // Verify the JWT credential with Google
      const ticket = await googleClient.verifyIdToken({
        idToken: credential,
        audience: googleClientId,
      });

      const payload = ticket.getPayload();

      if (!payload || !payload.email) {
        console.error("‚ùå Google OAuth: Payload inv√°lido ou email ausente");
        return res.status(401).json({ 
          success: false, 
          message: "Credencial do Google inv√°lida" 
        });
      }

      const { email, given_name, family_name, picture } = payload;

      console.log("‚úÖ Google OAuth: Token validado para", email);

      // Check if user already exists
      let user = await storage.getUserByEmail(email);

      if (!user) {
        // Create new user
        console.log("üìù Google OAuth: Criando novo usu√°rio");
        user = await storage.createUser({
          email: email,
          firstName: given_name || "User",
          lastName: family_name || "",
          profileImage: picture || "",
          password: "", // OAuth users don't have passwords
          username: (given_name || "user").toLowerCase().replace(/\s+/g, '') + Math.random().toString(36).substring(2, 5),
          phone: "",
          birthDate: "",
          gender: "",
          sexualOrientation: [],
          interestedIn: [],
          city: "",
          location: "",
          education: "",
          company: "",
          school: "",
          interests: [],
          bio: "",
          photos: [],
          isOnline: false,
          subscriptionType: "free",
          dailyLikes: 0,
        });
        console.log("‚úÖ Google OAuth: Usu√°rio criado com sucesso -", user.email);
      } else {
        console.log("‚úÖ Google OAuth: Usu√°rio existente encontrado -", user.email);
      }

      // Login user (create session)
      req.login(user, async (err) => {
        if (err) {
          console.error("‚ùå Google OAuth: Erro ao criar sess√£o", err);
          return res.status(500).json({ 
            success: false, 
            message: "Erro ao criar sess√£o" 
          });
        }

        try {
          // Fetch fresh user data
          let freshUser = await storage.getUserByEmail(email);

          if (!freshUser) {
            console.error("‚ùå Google OAuth: Usu√°rio n√£o encontrado ap√≥s login");
            return res.status(500).json({ 
              success: false, 
              message: "Erro ao buscar dados do usu√°rio" 
            });
          }

          // üîß AUTO-FIX: Corrigir dados incompletos automaticamente (dev + production)
          const needsFix = !freshUser.birthDate || !freshUser.gender || 
                          !freshUser.interestedIn || freshUser.interestedIn.length === 0 ||
                          (!freshUser.profileImage && freshUser.photos && freshUser.photos.length > 0);

          if (needsFix) {
            console.log("üîß AUTO-FIX: Detectados dados incompletos, corrigindo...");
            const autoFixData: any = {};

            // Corrigir profileImage se houver fotos mas sem profileImage
            if (!freshUser.profileImage && freshUser.photos && freshUser.photos.length > 0) {
              autoFixData.profileImage = freshUser.photos[0];
              console.log("üîß AUTO-FIX: Sincronizando profileImage com photos[0]");
            }

            // Se tiver dados cr√≠ticos vazios, mant√©m perfil incompleto para for√ßar onboarding
            if (Object.keys(autoFixData).length > 0) {
              await storage.updateUser(freshUser.id, autoFixData);
              const updatedUser = await storage.getUserByEmail(email);
              if (updatedUser) {
                freshUser = updatedUser;
                console.log("‚úÖ AUTO-FIX: Dados corrigidos automaticamente");
              }
            }
          }

          // Check if profile is complete
          const isProfileComplete = !!(
            freshUser.birthDate &&
            freshUser.gender &&
            freshUser.photos &&
            freshUser.photos.length >= 2 &&
            freshUser.interestedIn &&
            freshUser.interestedIn.length > 0
          );

          console.log("‚úÖ Google OAuth: Login bem-sucedido!");
          console.log("   - User ID:", freshUser.id);
          console.log("   - Email:", freshUser.email);
          console.log("   - Profile Complete:", isProfileComplete);

          // Save session explicitly
          req.session.save((saveErr) => {
            if (saveErr) {
              console.error("‚ùå Google OAuth: Erro ao salvar sess√£o", saveErr);
            } else {
              console.log("‚úÖ Google OAuth: Sess√£o salva com sucesso");
            }

            return res.json({
              success: true,
              message: "Login realizado com sucesso",
              user: {
                id: freshUser.id,
                email: freshUser.email,
                firstName: freshUser.firstName,
                username: freshUser.username,
              },
              isProfileComplete: isProfileComplete,
            });
          });
        } catch (error) {
          console.error("‚ùå Google OAuth: Erro ao buscar dados do usu√°rio", error);
          return res.status(500).json({ 
            success: false, 
            message: "Erro ao buscar dados do usu√°rio" 
          });
        }
      });
    } catch (error: any) {
      console.error("‚ùå Google OAuth: Erro na valida√ß√£o", error);

      // Check for specific error types
      if (error.message?.includes('Token used too late')) {
        return res.status(401).json({ 
          success: false, 
          message: "Token expirado. Por favor, tente fazer login novamente." 
        });
      }

      if (error.message?.includes('Invalid token signature')) {
        return res.status(401).json({ 
          success: false, 
          message: "Token inv√°lido. Por favor, tente fazer login novamente." 
        });
      }

      return res.status(500).json({ 
        success: false, 
        message: "Erro ao processar login com Google. Tente novamente." 
      });
    }
  });

  // Helper function to check if profile is complete
  function isProfileComplete(user: any): boolean {
    // ‚úÖ Perfil completo = birthDate + pelo menos 2 fotos
    // Gender e interestedIn s√£o opcionais - usu√°rio pode definir depois
    return !!(
      user.birthDate &&
      user.photos && user.photos.length >= 2
    );
  }

  // Check authentication status
  app.get("/api/auth/user", async (req, res) => {
    if (req.isAuthenticated() && req.user) {
      const sessionUser = req.user as any;

      // ‚úÖ CR√çTICO: Buscar dados atualizados do banco ao inv√©s de confiar na sess√£o
      const freshUser = await storage.getUser(sessionUser.id);

      if (!freshUser) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const profileComplete = isProfileComplete(freshUser);

      // ‚úÖ CORRE√á√ÉO CR√çTICA: Garantir que isProfileComplete √© sempre booleano
      const result = {
        ...freshUser,
        isProfileComplete: !!profileComplete // For√ßar convers√£o para boolean
      };

      res.json(result);
    } else {
      res.status(401).json({ message: "Not authenticated" });
    }
  });

  // Update user data (e.g., photos)
  app.patch("/api/user/update", async (req, res) => {
    try {
      if (!req.isAuthenticated() || !req.user) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = req.user as any;
      const userId = user.id;
      const { photos, ...otherData } = req.body;

      const updateData: any = { ...otherData };

      // ‚úÖ SINCRONIZAR: se photos foi atualizado, atualizar profileImage tamb√©m
      if (photos !== undefined && Array.isArray(photos)) {
        updateData.photos = photos;
        // Se h√° fotos, usar a primeira. Se n√£o h√° fotos, limpar profileImage
        updateData.profileImage = photos.length > 0 ? photos[0] : "";
        console.log(`üîµ ‚úÖ Sincronizando profileImage: ${photos.length > 0 ? 'photos[0]' : 'vazio'}`);
      }

      // Update user in database
      const updatedUser = await storage.updateUser(userId, updateData);

      // Update session with new data
      req.user = { ...req.user, ...updatedUser };

      res.json({ success: true, user: updatedUser });
    } catch (error) {
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Create account from OAuth session
  app.post("/api/auth/create-oauth-account", async (req, res) => {
    try {
      console.log("üîµ /api/auth/create-oauth-account called");
      console.log("üîµ req.isAuthenticated():", req.isAuthenticated());

      if (!req.isAuthenticated() || !req.user) {
        console.log("üî¥ User not authenticated or no user object");
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = req.user as any;
      console.log("üîµ User metadata:", {
        id: user.id,
        email: user.email,
        isNewUser: user.isNewUser,
        tempId: user.tempId
      });

      if (!user.isNewUser || !user.tempId) {
        console.log("üî¥ Invalid OAuth session - not new user or no tempId");
        console.log("üî¥ user.isNewUser:", user.isNewUser);
        console.log("üî¥ user.tempId:", user.tempId);
        return res.status(400).json({ message: "Invalid OAuth session" });
      }

      console.log("üîµ Creating OAuth account from tempId:", user.tempId);

      // Get temp user data
      const tempUser = OAuthStorage.getTempUser(user.tempId);
      if (!tempUser) {
        return res.status(400).json({ message: "Invalid or expired temp user" });
      }

      // Create actual user account
      const newUser = await storage.createUser({
        email: tempUser.email,
        firstName: tempUser.firstName,
        lastName: tempUser.lastName,
        profileImage: tempUser.profileImage,
        password: "", // OAuth users don't have passwords
        username: tempUser.firstName?.toLowerCase() || "user"
      });

      console.log("üîµ OAuth account created successfully:", newUser.id);

      // Update session with real user data
      req.login({ ...newUser, isOAuthUser: true, isNewUser: false }, (err) => {
        if (err) {
          console.error("üî¥ Error setting up session:", err);
          return res.status(500).json({ message: "Session error" });
        }
        res.json({ 
          success: true, 
          message: "Account created successfully", 
          user: {
            id: newUser.id,
            email: newUser.email,
            firstName: newUser.firstName,
            lastName: newUser.lastName,
            profileImage: newUser.profileImage
          }
        });
      });
    } catch (error) {
      console.error("üî¥ Error creating OAuth account:", error);
      res.status(500).json({ message: "Failed to create account" });
    }
  });

  // Test OAuth system (Google)
  app.get("/api/test-oauth", (req, res) => {
    try {
      console.log("üîµ Testing Google OAuth system");

      // Create a test temp user
      const tempId = OAuthStorage.storeTempUser("test@gmail.com", {
        provider: "google",
        email: "test@gmail.com",
        firstName: "Test",
        lastName: "User",
        profileImage: "https://lh3.googleusercontent.com/a/default-user=s96-c"
      });

      // Create a temp user session
      const tempUser = {
        isOAuthUser: true,
        isNewUser: true,
        tempId,
        provider: "google",
        email: "test@gmail.com",
        firstName: "Test",
        lastName: "User",
        profileImage: "https://lh3.googleusercontent.com/a/default-user=s96-c"
      };

      // Login the temp user
      req.login(tempUser, (err) => {
        if (err) {
          console.error("üî¥ Error logging in temp user:", err);
          return res.status(500).json({ message: "Login error" });
        }
        console.log("üîµ Test Google OAuth user logged in successfully");
        res.json({ 
          message: "Google OAuth test successful",
          tempId,
          redirectTo: "/discover"
        });
      });
    } catch (error) {
      console.error("üî¥ Test OAuth error:", error);
      res.status(500).json({ message: "Test failed" });
    }
  });

  // Test Facebook OAuth system
  app.get("/api/test-facebook-oauth", (req, res) => {
    try {
      console.log("üîµ Testing Facebook OAuth system");

      // Create a test temp user for Facebook
      const tempId = OAuthStorage.storeTempUser("test@facebook.local", {
        provider: "facebook",
        email: "test@facebook.local",
        firstName: "Facebook",
        lastName: "User",
        profileImage: "https://graph.facebook.com/123456789/picture?type=large"
      });

      // Create a temp user session
      const tempUser = {
        isOAuthUser: true,
        isNewUser: true,
        tempId,
        provider: "facebook",
        email: "test@facebook.local",
        firstName: "Facebook",
        lastName: "User",
        profileImage: "https://graph.facebook.com/123456789/picture?type=large"
      };

      // Login the temp user
      req.login(tempUser, (err) => {
        if (err) {
          console.error("üî¥ Error logging in Facebook temp user:", err);
          return res.status(500).json({ message: "Login error" });
        }
        console.log("üîµ Test Facebook OAuth user logged in successfully");
        res.json({ 
          message: "Facebook OAuth test successful",
          tempId,
          redirectTo: "/discover"
        });
      });
    } catch (error) {
      console.error("üî¥ Test Facebook OAuth error:", error);
      res.status(500).json({ message: "Test failed" });
    }
  });

  // Logout route
  app.post("/api/auth/logout", (req, res) => {
    req.logout((err) => {
      if (err) {
        return res.status(500).json({ message: "Error logging out" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  // Check authentication status
  app.get("/api/auth/me", async (req, res) => {
    try {
      if (!req.isAuthenticated() || !req.user) {
        return res.status(401).json({ authenticated: false, message: "Not authenticated" });
      }

      const user = req.user as any;

      // Get user profile if exists
      let profile = null;
      if (user.id) {
        profile = await storage.getProfile(user.id);
      }

      res.json({
        authenticated: true,
        user: {
          id: user.id,
          email: user.email,
          username: user.username,
        },
        profile: profile ? {
          id: profile.id,
          name: profile.name,
          age: profile.age,
          photos: profile.photos,
        } : null
      });
    } catch (error) {
      console.error("üî¥ Error checking auth:", error);
      res.status(500).json({ message: "Server error" });
    }
  });

  // Traditional auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      // Aceitar email, password, username (opcional) e phone (opcional)
      const { email, password, username: providedUsername, phone } = req.body;

      // Validar password (m√≠nimo 6 caracteres)
      if (!password || password.length < 6) {
        return res.status(400).json({ message: "Senha deve ter pelo menos 6 caracteres" });
      }

      const existingUser = await storage.getUserByEmail(email);

      if (existingUser) {
        return res.status(400).json({ message: "Usu√°rio j√° existe" });
      }

      // Usar username fornecido ou gerar baseado no email
      let username = providedUsername;
      if (!username) {
        const baseUsername = email.split('@')[0].toLowerCase().replace(/[^a-z0-9]/g, '');
        username = baseUsername;
        let attempts = 0;

        while (attempts < 10) {
          const existingUsername = await storage.getUserByUsername(username);
          if (!existingUsername) break;

          username = baseUsername + Math.floor(Math.random() * 9999).toString();
          attempts++;
        }
      }

      // Hash da senha antes de salvar
      const hashedPassword = await bcrypt.hash(password, 10);

      // Criar usu√°rio com dados m√≠nimos + valores padr√£o
      const user = await storage.createUser({
        email,
        password: hashedPassword,
        username,
        firstName: "",
        lastName: "",
        profileImage: "",
        phone: phone || "",
        birthDate: "",
        gender: "",
        sexualOrientation: [],
        interestedIn: [],
        city: "",
        location: "",
        education: "",
        company: "",
        school: "",
        interests: [],
        bio: "",
        photos: [],
        isOnline: false,
        subscriptionType: "free",
        dailyLikes: 0,
      });

      // N√£o criar perfil automaticamente - usu√°rio vai preencher depois
      console.log("üîµ ‚úÖ Usu√°rio registrado:", user.email, "| Phone:", phone || "N/A");

      // Fazer login autom√°tico ap√≥s registro usando req.login
      req.login(user, (err) => {
        if (err) {
          console.error("üî¥ Erro no login autom√°tico ap√≥s registro:", err);
          return res.json({ user: { id: user.id, username: user.username, email: user.email }, loginError: true });
        }

        // ‚úÖ Perfil nunca est√° completo logo ap√≥s registro
        const profileComplete = false;

        console.log("üîµ ‚úÖ Usu√°rio logado automaticamente ap√≥s registro:", user.email);
        res.json({ 
          user: { id: user.id, username: user.username, email: user.email },
          loggedIn: true,
          isProfileComplete: profileComplete,
          message: "Conta criada e login realizado com sucesso"
        });
      });
    } catch (error) {
      console.error("üî¥ Erro no registro:", error);
      if (error instanceof Error && error.message.includes('duplicate key')) {
        res.status(400).json({ message: "Usu√°rio j√° existe" });
      } else {
        res.status(400).json({ message: "Dados inv√°lidos" });
      }
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { identifier, email, password } = req.body;
      const loginIdentifier = identifier || email; // Aceitar ambos os campos
      console.log("üîµ Login attempt for:", loginIdentifier);

      // Tentar buscar por email OU por telefone
      let user = await storage.getUserByEmail(loginIdentifier);

      // Se n√£o encontrar por email, tentar por telefone
      if (!user) {
        console.log("üîµ Not found by email, trying phone...");
        user = await storage.getUserByPhone(loginIdentifier); // O campo pode conter telefone
      }

      if (!user) {
        console.log("üî¥ User not found (email/phone):", loginIdentifier);
        return res.status(404).json({ 
          message: "Usu√°rio n√£o encontrado", 
          userNotFound: true 
        });
      }

      console.log("üîµ User found, checking password");
      const isPasswordValid = await bcrypt.compare(password, user.password);
      if (!isPasswordValid) {
        console.log("üî¥ Password mismatch for:", email);
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Fazer login usando sess√£o
      req.login(user, (err) => {
        if (err) {
          console.error("üî¥ Erro no login:", err);
          return res.status(500).json({ message: "Login session error" });
        }

        // Salvar sess√£o explicitamente antes de responder
        req.session.save((saveErr) => {
          if (saveErr) {
            console.error("üî¥ Erro ao salvar sess√£o:", saveErr);
            return res.status(500).json({ message: "Session save error" });
          }

          // ‚úÖ Verificar se perfil est√° completo
          const profileComplete = isProfileComplete(user);

          console.log("üîµ ‚úÖ Usu√°rio logado e sess√£o salva:", user.email);
          console.log("üîµ Profile complete:", profileComplete);

          res.json({ 
            user: { id: user.id, username: user.username, email: user.email },
            loggedIn: true,
            isProfileComplete: profileComplete,
            message: "Login realizado com sucesso"
          });
        });
