import { useState, useRef, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation, useParams } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ArrowLeft, Send, Heart, Phone, Video, MoreHorizontal, Image as ImageIcon, Smile, X } from "lucide-react";
import { formatTimeAgo } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";
import type { Message } from "@shared/schema";
import BottomNavigation from "@/components/BottomNavigation";

// Emojis mais usados
const POPULAR_EMOJIS = [
  'üòä', 'üòÇ', '‚ù§Ô∏è', 'üî•', 'üëç', 'üòç', 'ü•∞', 'üòò', 
  'üíï', 'üíñ', 'üòé', 'ü§ó', 'üòâ', 'üòú', 'üôå', 'üëè',
  'üí™', 'üéâ', '‚ú®', 'üåü', 'üíØ', 'üôè', 'üëÄ', 'üò≥'
];

export default function Chat() {
  const params = useParams();
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const matchId = parseInt(params.matchId || "0");
  const [messageText, setMessageText] = useState("");
  const [ws, setWs] = useState<WebSocket | null>(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  // Buscar dados REAIS do usu√°rio atual
  const { data: currentUser, isError: userError, isLoading: isLoadingUser } = useQuery({
    queryKey: ['/api/auth/user'],
    queryFn: async () => {
      const res = await fetch('/api/auth/user', { credentials: 'include' });
      if (!res.ok) throw new Error('Not authenticated');
      return res.json();
    },
    retry: 1,
  });

  // Buscar dados REAIS do match
  const { data: matchData } = useQuery({
    queryKey: [`/api/match/${matchId}`],
    queryFn: async () => {
      const res = await fetch(`/api/match/${matchId}`, { credentials: 'include' });
      if (!res.ok) throw new Error('Failed to fetch match');
      return res.json();
    },
    enabled: !!matchId,
  });

  const currentProfile = matchData?.profile || null;
  const currentUserId = currentUser?.id;

  const { data: messages = [], isLoading } = useQuery<Message[]>({
    queryKey: [`/api/messages/${matchId}`],
    enabled: !!matchId,
    refetchInterval: 3000,
  });

  // WebSocket para mensagens em tempo real
  useEffect(() => {
    if (!matchId || !currentUserId) return;

    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const wsUrl = `${protocol}//${window.location.host}`;
    const websocket = new WebSocket(wsUrl);

    websocket.onopen = () => {
      console.log('‚úÖ WebSocket conectado');
      websocket.send(JSON.stringify({ type: 'subscribe', matchId }));
    };

    websocket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);

        if (data.type === 'new_message' && data.matchId === matchId) {
          queryClient.invalidateQueries({ queryKey: [`/api/messages/${matchId}`] });

          if (data.message.senderId !== currentUserId) {
            toast({
              title: "Nova mensagem! üí¨",
              description: data.message.content.substring(0, 50) + (data.message.content.length > 50 ? '...' : ''),
              duration: 3000,
            });

            if ('Notification' in window && Notification.permission === 'granted') {
              new Notification(`${currentProfile?.name || 'Match'}`, {
                body: data.message.content,
                icon: currentProfile?.photos?.[0],
              });
            }
          }
        }
      } catch (error) {
        console.error('Erro ao processar mensagem WebSocket:', error);
      }
    };

    websocket.onerror = (error) => {
      console.error('‚ùå Erro no WebSocket:', error);
    };

    websocket.onclose = () => {
      console.log('üî¥ WebSocket desconectado');
    };

    setWs(websocket);

    return () => {
      websocket.close();
    };
  }, [matchId, currentUserId, currentProfile, queryClient, toast]);

  // Pedir permiss√£o para notifica√ß√µes
  useEffect(() => {
    if ('Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission();
    }
  }, []);

  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      const response = await fetch("/api/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify({
          matchId,
          senderId: currentUserId,
          content,
        })
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: 'Erro ao enviar mensagem' }));
        throw new Error(errorData.error || 'Erro ao enviar mensagem');
      }

      return await response.json();
    },
    onSuccess: (newMessage) => {
      queryClient.invalidateQueries({ queryKey: [`/api/messages/${matchId}`] });
      queryClient.invalidateQueries({ queryKey: ['/api/matches'] });
      setMessageText("");

      if (ws && ws.readyState === WebSocket.OPEN) {
        ws.send(JSON.stringify({
          type: 'send_message',
          matchId,
          message: newMessage
        }));
      }
    },
    onError: (error: Error) => {
      console.error('‚ùå Erro ao enviar mensagem:', error);
      toast({
        title: "Erro ao enviar",
        description: error.message || "N√£o foi poss√≠vel enviar a mensagem. Tente novamente.",
        variant: "destructive",
        duration: 3000,
      });
    },
  });

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (messageText.trim() && !sendMessageMutation.isPending) {
      sendMessageMutation.mutate(messageText.trim());
    }
  };

  const handleEmojiClick = (emoji: string) => {
    setMessageText(prev => prev + emoji);
    setShowEmojiPicker(false);
  };

  const handleImageSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Verificar tipo de arquivo
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Arquivo inv√°lido",
        description: "Por favor, selecione uma imagem.",
        variant: "destructive",
        duration: 3000,
      });
      return;
    }

    // Verificar tamanho (m√°ximo 5MB)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "Arquivo muito grande",
        description: "A imagem deve ter no m√°ximo 5MB.",
        variant: "destructive",
        duration: 3000,
      });
      return;
    }

    // Converter para base64 e enviar
    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64String = reader.result as string;

      try {
        const response = await fetch("/api/messages/image", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
          body: JSON.stringify({
            matchId,
            senderId: currentUserId,
            imageData: base64String,
          })
        });

        if (!response.ok) {
          throw new Error('Erro ao enviar imagem');
        }

        const newMessage = await response.json();

        // Atualizar lista de mensagens
        queryClient.invalidateQueries({ queryKey: [`/api/messages/${matchId}`] });
        queryClient.invalidateQueries({ queryKey: ['/api/matches'] });

        if (ws && ws.readyState === WebSocket.OPEN) {
          ws.send(JSON.stringify({
            type: 'send_message',
            matchId,
            message: newMessage
          }));
        }

        toast({
          title: "Imagem enviada! üì∏",
          description: "Sua foto foi enviada com sucesso.",
          duration: 2000,
        });
      } catch (error) {
        console.error('Erro ao enviar imagem:', error);
        toast({
          title: "Erro ao enviar",
          description: "N√£o foi poss√≠vel enviar a imagem. Tente novamente.",
          variant: "destructive",
          duration: 3000,
        });
      }
    };

    reader.readAsDataURL(file);

    // Limpar o input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const goBack = () => {
    setLocation("/messages");
  };

  // Verifica√ß√£o de autentica√ß√£o
  if (userError) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex items-center justify-center">
        <div className="text-center px-4">
          <p className="text-white text-xl mb-4">Voc√™ precisa fazer login para acessar o chat</p>
          <button 
            onClick={() => setLocation('/login')}
            className="bg-gradient-to-r from-pink-500 to-purple-600 text-white px-8 py-3 rounded-full font-semibold"
          >
            Ir para Login
          </button>
        </div>
      </div>
    );
  }

  if (isLoading || !currentProfile || !currentUser) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin w-12 h-12 border-4 border-pink-500 border-t-transparent rounded-full mx-auto mb-4"></div>
          <p className="text-white text-lg">Carregando conversa...</p>
        </div>
      </div>
    );
  }

  const profilePhoto = currentProfile.photos?.[0] || `https://ui-avatars.com/api/?name=${encodeURIComponent(currentProfile.name || 'Match')}&background=ec4899&color=fff&size=400`;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex flex-col pb-20">
      {/* Chat Header */}
      <header className="bg-white/10 backdrop-blur-xl border-b border-white/20 sticky top-0 z-40 shadow-lg">
        <div className="flex items-center px-4 py-3">
          <Button 
            variant="ghost" 
            size="icon"
            onClick={goBack}
            className="mr-3 text-white hover:bg-white/20 rounded-full transition-all"
            data-testid="button-back"
          >
            <ArrowLeft className="w-6 h-6" />
          </Button>

          <div className="flex items-center space-x-3 flex-1 cursor-pointer" onClick={() => setLocation(`/match-profile/${currentProfile.userId}`)}>
            <div className="relative">
              <img
                src={profilePhoto}
                alt={currentProfile.name}
                className="w-12 h-12 rounded-full object-cover border-2 border-pink-500 shadow-lg"
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(currentProfile.name || 'Match')}&background=ec4899&color=fff&size=100`;
                }}
              />
              <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 border-2 border-blue-900 rounded-full"></div>
            </div>
            <div>
              <h2 className="font-bold text-white text-lg">{currentProfile.name}</h2>
              <p className="text-sm text-green-400 font-medium">Online agora</p>
            </div>
          </div>

          <div className="flex items-center space-x-1">
            <Button 
              variant="ghost" 
              size="icon" 
              className="text-white hover:bg-white/20 rounded-full transition-all"
              data-testid="button-phone"
            >
              <Phone className="w-5 h-5" />
            </Button>
            <Button 
              variant="ghost" 
              size="icon" 
              className="text-white hover:bg-white/20 rounded-full transition-all"
              data-testid="button-video"
            >
              <Video className="w-5 h-5" />
            </Button>
            <Button 
              variant="ghost" 
              size="icon" 
              className="text-white hover:bg-white/20 rounded-full transition-all"
              data-testid="button-more"
            >
              <MoreHorizontal className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </header>

      {/* Messages Area */}
      <main className="flex-1 flex flex-col min-h-0">
        <div className="flex-1 overflow-y-auto p-4 space-y-3 pb-4">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full text-center py-12">
              <div className="w-20 h-20 bg-gradient-to-br from-pink-500 to-purple-600 rounded-full flex items-center justify-center mb-6 shadow-2xl animate-pulse">
                <Heart className="w-10 h-10 text-white" />
              </div>
              <h3 className="text-2xl font-bold text-white mb-3">
                √â um Match! üéâ
              </h3>
              <p className="text-white/80 text-lg mb-2">
                Voc√™s se curtiram mutuamente!
              </p>
              <p className="text-white/60 max-w-sm">
                Comece uma conversa e descubra o que voc√™s t√™m em comum.
              </p>
            </div>
          ) : (
            <>
              {messages.map((message, index) => {
                const isOwnMessage = message.senderId === currentUserId;
                const showAvatar = !isOwnMessage && (index === 0 || messages[index - 1].senderId !== message.senderId);

                return (
                  <div
                    key={message.id}
                    className={`flex items-end gap-2 ${isOwnMessage ? "justify-end" : "justify-start"}`}
                    data-testid={`message-${message.id}`}
                  >
                    {!isOwnMessage && (
                      <div className="w-8 h-8 flex-shrink-0">
                        {showAvatar && (
                          <img
                            src={profilePhoto}
                            alt={currentProfile.name}
                            className="w-8 h-8 rounded-full object-cover border border-white/20"
                          />
                        )}
                      </div>
                    )}

                    <div
                      className={`max-w-[70%] px-4 py-3 rounded-2xl shadow-lg ${
                        isOwnMessage
                          ? "bg-gradient-to-r from-pink-500 to-purple-600 text-white rounded-br-sm"
                          : "bg-white/15 backdrop-blur-sm text-white rounded-bl-sm border border-white/10"
                      }`}
                    >
                      {(message as any).imageUrl ? (
                        <div className="space-y-2">
                          <img 
                            src={(message as any).imageUrl} 
                            alt="Imagem enviada" 
                            className="rounded-lg max-w-full h-auto max-h-64 object-cover"
                          />
                          {message.content !== "üì∑ Imagem" && (
                            <p className="text-sm leading-relaxed break-words">{message.content}</p>
                          )}
                        </div>
                      ) : (
                        <p className="text-sm leading-relaxed break-words">{message.content}</p>
                      )}
                      <p
                        className={`text-xs mt-1.5 ${
                          isOwnMessage ? "text-white/80" : "text-white/60"
                        }`}
                      >
                        {formatTimeAgo(new Date(message.createdAt))}
                      </p>
                    </div>
                  </div>
                );
              })}
              <div ref={messagesEndRef} />
            </>
          )}
        </div>

        {/* Emoji Picker */}
        {showEmojiPicker && (
          <div className="mx-4 mb-2 bg-white/95 backdrop-blur-sm rounded-2xl p-4 shadow-2xl border border-white/20 relative">
            <Button
              variant="ghost"
              size="icon"
              onClick={() => setShowEmojiPicker(false)}
              className="absolute top-2 right-2 w-8 h-8 rounded-full hover:bg-gray-200"
            >
              <X className="w-4 h-4 text-gray-600" />
            </Button>
            <h3 className="text-sm font-semibold text-gray-700 mb-3">Escolha um emoji</h3>
            <div className="grid grid-cols-8 gap-2">
              {POPULAR_EMOJIS.map((emoji, index) => (
                <button
                  key={index}
                  onClick={() => handleEmojiClick(emoji)}
                  className="text-2xl hover:bg-gray-200 rounded-lg p-2 transition-colors"
                  data-testid={`emoji-${index}`}
                >
                  {emoji}
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Message Input */}
        <div className="border-t border-white/20 bg-white/5 backdrop-blur-xl p-4 shadow-2xl flex-shrink-0">
          <form onSubmit={handleSendMessage} className="flex items-center space-x-3">
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleImageSelect}
              className="hidden"
            />

            <Button
              type="button"
              variant="ghost"
              size="icon"
              onClick={() => setShowEmojiPicker(!showEmojiPicker)}
              className={`text-white hover:bg-white/10 rounded-full flex-shrink-0 transition-all ${showEmojiPicker ? 'bg-white/20' : ''}`}
              data-testid="button-emoji"
            >
              <Smile className="w-6 h-6" />
            </Button>

            <Input
              value={messageText}
              onChange={(e) => setMessageText(e.target.value)}
              placeholder="Digite sua mensagem..."
              className="flex-1 bg-white/10 border-white/30 text-white placeholder-white/60 rounded-full px-5 py-3 focus:bg-white/15 focus:border-pink-500 transition-all"
              disabled={sendMessageMutation.isPending}
              data-testid="input-message"
            />

            <Button
              type="button"
              variant="ghost"
              size="icon"
              onClick={() => fileInputRef.current?.click()}
              className="text-white hover:bg-white/10 rounded-full flex-shrink-0 transition-all"
              data-testid="button-image"
            >
              <ImageIcon className="w-6 h-6" />
            </Button>

            <Button
              type="submit"
              disabled={!messageText.trim() || sendMessageMutation.isPending}
              className="bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white rounded-full w-12 h-12 p-0 shadow-lg disabled:opacity-50 disabled:cursor-not-allowed transition-all"
              data-testid="button-send"
            >
              {sendMessageMutation.isPending ? (
                <div className="animate-spin w-5 h-5 border-2 border-white border-t-transparent rounded-full"></div>
              ) : (
                <Send className="w-5 h-5" />
              )}
            </Button>
          </form>
        </div>
      </main>

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 left-0 right-0 z-50">
        <BottomNavigation />
      </div>
    </div>
  );
}

client/src/pages/admin/admin-notificatio.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import AdminLayout from "@/components/admin/AdminLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { 
  Bell, 
  Send,
  Users,
  MessageSquare,
  AlertTriangle
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function AdminNotifications() {
  const [, setLocation] = useLocation();
  const [notificationText, setNotificationText] = useState("");
  const [targetType, setTargetType] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    const adminToken = localStorage.getItem("adminToken");
    if (!adminToken) {
      setLocation("/admin");
    }
  }, [setLocation]);

  const { data: notifications, isLoading } = useQuery({
    queryKey: ['/api/admin/notifications'],
    queryFn: async () => {
      const response = await fetch('/api/admin/notifications');
      if (!response.ok) {
        throw new Error('Failed to fetch notifications');
      }
      return response.json();
    }
  });

  const sendNotificationMutation = useMutation({
    mutationFn: async (data: { message: string; targetType: string }) => {
      const response = await fetch('/api/admin/notifications/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });
      if (!response.ok) {
        throw new Error('Failed to send notification');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/notifications'] });
      setNotificationText("");
      toast({
        title: "Sucesso",
        description: "Notifica√ß√£o enviada com sucesso"
      });
    }
  });

  const handleSendNotification = () => {
    if (!notificationText.trim()) {
      toast({
        title: "Erro",
        description: "Digite uma mensagem para enviar",
        variant: "destructive"
      });
      return;
    }

    sendNotificationMutation.mutate({
      message: notificationText,
      targetType
    });
  };

  if (isLoading) {
    return (
      <AdminLayout title="Gerenciar Notifica√ß√µes">
        <div className="animate-pulse space-y-4">
          {Array.from({ length: 5 }).map((_, i) => (
            <div key={i} className="h-20 bg-blue-800/50 rounded-lg"></div>
          ))}
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout title="Gerenciar Notifica√ß√µes">
      <div className="space-y-4 w-full">
        {/* Send Notification */}
        <Card className="p-4 bg-blue-800/50 backdrop-blur-sm border-blue-700/50">
          <h3 className="text-lg font-semibold text-white mb-4">Enviar Notifica√ß√£o</h3>
          <div className="space-y-4">
            <div>
              <label className="text-blue-200 text-sm mb-2 block">P√∫blico Alvo</label>
              <select
                value={targetType}
                onChange={(e) => setTargetType(e.target.value)}
                className="w-full p-2 bg-blue-700/50 border border-blue-600/50 rounded text-white"
              >
                <option value="all">Todos os usu√°rios</option>
                <option value="premium">Usu√°rios Premium</option>
                <option value="vip">Usu√°rios VIP</option>
                <option value="free">Usu√°rios Gratuitos</option>
              </select>
            </div>
            <div>
              <label className="text-blue-200 text-sm mb-2 block">Mensagem</label>
              <Input
                value={notificationText}
                onChange={(e) => setNotificationText(e.target.value)}
                placeholder="Digite sua mensagem..."
                className="bg-blue-700/50 border-blue-600/50 text-white placeholder-blue-300"
              />
            </div>
            <Button
              onClick={handleSendNotification}
              disabled={sendNotificationMutation.isPending}
              className="w-full bg-gradient-to-r from-pink-500 to-purple-600 text-white hover:from-pink-600 hover:to-purple-700"
            >
              <Send className="w-4 h-4 mr-2" />
              {sendNotificationMutation.isPending ? 'Enviando...' : 'Enviar Notifica√ß√£o'}
            </Button>
          </div>
        </Card>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
          <Card className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50">
            <div className="flex items-center gap-3">
              <Bell className="w-6 h-6 text-blue-400" />
              <div>
                <p className="text-blue-200 text-sm">Total Enviadas</p>
                <p className="text-white text-lg font-bold">{notifications?.length || 0}</p>
              </div>
            </div>
          </Card>

          <Card className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50">
            <div className="flex items-center gap-3">
              <Users className="w-6 h-6 text-green-400" />
              <div>
                <p className="text-blue-200 text-sm">Usu√°rios Alcan√ßados</p>
                <p className="text-white text-lg font-bold">
                  {notifications?.reduce((acc: number, n: any) => acc + (n.recipientsCount || 0), 0) || 0}
                </p>
              </div>
            </div>
          </Card>

          <Card className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50">
            <div className="flex items-center gap-3">
              <MessageSquare className="w-6 h-6 text-purple-400" />
              <div>
                <p className="text-blue-200 text-sm">Taxa de Abertura</p>
                <p className="text-white text-lg font-bold">78%</p>
              </div>
            </div>
          </Card>
        </div>

        {/* Notifications History */}
        <Card className="p-4 bg-blue-800/50 backdrop-blur-sm border-blue-700/50">
          <h3 className="text-lg font-semibold text-white mb-4">Hist√≥rico de Notifica√ß√µes</h3>
          <div className="space-y-3">
            {notifications && notifications.length > 0 ? (
              notifications.map((notification: any) => (
                <div key={notification.id} className="flex items-center justify-between p-3 bg-blue-700/30 rounded-lg">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full flex items-center justify-center">
                      <Bell className="w-5 h-5 text-white" />
                    </div>
                    <div>
                      <p className="text-white font-medium">{notification.message || 'Notifica√ß√£o'}</p>
                      <p className="text-blue-200 text-sm">
                        Para: {notification.targetType === 'all' ? 'Todos os usu√°rios' : notification.targetType}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <Badge className="bg-blue-500/20 text-blue-300 border-blue-500/30 mb-1">
                      {notification.recipientsCount || 0} usu√°rios
                    </Badge>
                    <p className="text-blue-200 text-xs">
                      {notification.createdAt ? new Date(notification.createdAt).toLocaleDateString('pt-BR') : 'Hoje'}
                    </p>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center py-8">
                <Bell className="w-12 h-12 text-blue-400 mx-auto mb-3" />
                <p className="text-blue-200">Nenhuma notifica√ß√£o enviada</p>
              </div>
            )}
          </div>
        </Card>
      </div>
    </AdminLayout>
  );
}

client/src/pages/super-likes.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Star, Crown, ArrowLeft, Zap, Heart } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface SuperLikeCard {
  id: string;
  name: string;
  age: number;
  photo: string;
  bio: string;
  location: string;
  superLikedAt: string;
  isRead: boolean;
}

export default function SuperLikes() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [superLikes, setSuperLikes] = useState<SuperLikeCard[]>([]);
  const [loading, setLoading] = useState(true);
  const [superLikesCount, setSuperLikesCount] = useState(3);

  useEffect(() => {
    // Simulated data - in production would come from API
    const mockSuperLikes: SuperLikeCard[] = [
      {
        id: "sl1",
        name: "Sofia Martins",
        age: 27,
        photo: "https://images.unsplash.com/photo-1494790108755-2616b48ec4db?w=400&h=400&fit=crop",
        bio: "M√©dica apaixonada por viagens e fotografia. Sempre em busca de novas aventuras!",
        location: "S√£o Paulo, SP",
        superLikedAt: "1 hora atr√°s",
        isRead: false
      },
      {
        id: "sl2",
        name: "Lucas Ferreira",
        age: 32,
        photo: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=400&fit=crop",
        bio: "Arquiteto que adora arte, m√∫sica e bons vinhos. Vamos descobrir a cidade juntos?",
        location: "Rio de Janeiro, RJ",
        superLikedAt: "3 horas atr√°s",
        isRead: true
      }
    ];

    setTimeout(() => {
      setSuperLikes(mockSuperLikes);
      setLoading(false);
    }, 1000);
  }, []);

  const handleSuperLike = (profileId: string) => {
    if (superLikesCount <= 0) {
      toast({
        title: "Sem Super Likes",
        description: "Voc√™ n√£o tem Super Likes dispon√≠veis hoje. Assine Premium para mais!",
        variant: "destructive",
      });
      return;
    }

    setSuperLikesCount(prev => prev - 1);
    toast({
      title: "Super Like enviado! ‚≠ê",
      description: "Seu perfil foi destacado para essa pessoa!",
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-pink-900 flex items-center justify-center">
        <div className="text-center text-white">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p>Carregando Super Likes...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-pink-900 p-4">
      <div className="max-w-md mx-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setLocation("/discover")}
              className="text-white hover:bg-white/10"
            >
              <ArrowLeft className="w-4 h-4" />
            </Button>
            <div>
              <h1 className="text-xl font-bold text-white flex items-center gap-2">
                <Star className="w-5 h-5 text-yellow-400" />
                Super Likes
              </h1>
              <p className="text-white/60 text-sm">Destaque-se para algu√©m especial</p>
            </div>
          </div>
          <Badge variant="secondary" className="bg-yellow-500/20 text-yellow-200 border-yellow-400/30">
            {superLikesCount} restantes
          </Badge>
        </div>

        {/* Super Likes Counter */}
        <Card className="bg-gradient-to-r from-yellow-500/20 to-orange-500/20 border-yellow-400/30 text-white mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-lg">
              <Star className="w-5 h-5 text-yellow-400" />
              Seus Super Likes
            </CardTitle>
            <CardDescription className="text-white/70">
              Voc√™ tem {superLikesCount} Super Likes para hoje
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex items-center justify-between">
              <div className="text-sm text-white/80">
                Super Likes fazem voc√™ aparecer em destaque!
              </div>
              <Button
                onClick={() => setLocation("/premium")}
                variant="outline"
                className="border-yellow-400/50 text-yellow-200 hover:bg-yellow-400/10"
              >
                <Crown className="w-4 h-4 mr-2" />
                Mais
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Received Super Likes */}
        {superLikes.length > 0 && (
          <div className="mb-6">
            <h2 className="text-white font-semibold mb-4 flex items-center gap-2">
              <Heart className="w-5 h-5 text-pink-400" />
              Quem te deu Super Like
            </h2>
            <div className="space-y-3">
              {superLikes.map((superLike) => (
                <Card key={superLike.id} className="bg-white/10 border-white/20 text-white">
                  <CardContent className="p-4">
                    <div className="flex items-center gap-4">
                      <div className="relative">
                        <img
                          src={superLike.photo}
                          alt={superLike.name}
                          className="w-16 h-16 rounded-full object-cover"
                        />
                        <div className="absolute -top-1 -right-1 bg-yellow-500 text-white rounded-full p-1">
                          <Star className="w-3 h-3 fill-current" />
                        </div>
                        {!superLike.isRead && (
                          <div className="absolute -top-1 -left-1 bg-pink-500 w-3 h-3 rounded-full animate-pulse"></div>
