export default function AdminUserDetail() {
  const { id } = useParams();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    const adminToken = localStorage.getItem("adminToken");
    if (!adminToken) {
      setLocation("/admin");
    }
  }, [setLocation]);

  const { data: user, isLoading, refetch } = useQuery({
    queryKey: ['/api/admin/user-details', id],
    queryFn: async () => {
      const response = await fetch(`/api/admin/user-details/${id}`);
      if (!response.ok) throw new Error('Failed to fetch user details');
      return response.json();
    }
  });

  // Mutations for user actions
  const suspendUserMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/admin/users/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'deactivate' })
      });
      if (!response.ok) throw new Error('Failed to suspend user');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/user-details', id] });
      toast({
        title: "‚úÖ Usu√°rio Suspenso",
        description: "O usu√°rio foi suspenso com sucesso",
      });
      refetch();
    },
    onError: () => {
      toast({
        title: "‚ùå Erro",
        description: "Falha ao suspender usu√°rio",
        variant: "destructive"
      });
    }
  });

  const reactivateUserMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/admin/users/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'activate' })
      });
      if (!response.ok) throw new Error('Failed to reactivate user');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/user-details', id] });
      toast({
        title: "‚úÖ Usu√°rio Reativado",
        description: "O usu√°rio foi reativado com sucesso",
      });
      refetch();
    },
    onError: () => {
      toast({
        title: "‚ùå Erro",
        description: "Falha ao reativar usu√°rio",
        variant: "destructive"
      });
    }
  });

  const verifyUserMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/admin/users/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action: 'verify' })
      });
      if (!response.ok) throw new Error('Failed to verify user');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/user-details', id] });
      toast({
        title: "‚úÖ Usu√°rio Verificado",
        description: "O usu√°rio foi verificado com sucesso",
      });
      refetch();
    },
    onError: () => {
