import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import AdminLayout from "@/components/admin/AdminLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { 
  Settings, 
  Globe, 
  Shield,
  Bell,
  Save,
  Zap,
  Heart,
  AlertTriangle,
  Image,
  UserCheck,
  MapPin,
  TrendingUp
} from "lucide-react";
import type { AppSetting } from "@shared/schema";

export default function AdminSettingsNew() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
      setLocation("/admin/login");
    }
  }, [setLocation]);

  const { data: settings = [], isLoading } = useQuery<AppSetting[]>({
    queryKey: ['/api/admin/app-settings'],
    queryFn: async () => {
      const response = await fetch('/api/admin/app-settings', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('adminToken')}`
        }
      });
      if (!response.ok) throw new Error('Failed to fetch settings');
      return response.json();
    }
  });

  const updateSettingMutation = useMutation({
    mutationFn: async ({ key, value }: { key: string, value: string }) => {
      const response = await fetch('/api/admin/app-settings', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('adminToken')}`
        },
        body: JSON.stringify({ key, value })
      });

      if (!response.ok) throw new Error('Failed to update setting');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/app-settings'] });
    }
  });

  const getSetting = (key: string): string => {
    const setting = settings.find(s => s.key === key);
    return setting?.value || '';
  };

  const updateSetting = async (key: string, value: string | boolean | number) => {
    const stringValue = String(value);
    await updateSettingMutation.mutateAsync({ key, value: stringValue });
  };

  const saveAllSettings = async () => {
    setIsSaving(true);
    try {
      toast({
        title: "‚úÖ Configura√ß√µes Salvas",
        description: "Todas as altera√ß√µes foram salvas com sucesso",
      });
    } catch (error) {
      toast({
        title: "‚ùå Erro",
        description: "Falha ao salvar configura√ß√µes",
        variant: "destructive"
      });
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return (
      <AdminLayout title="Configura√ß√µes do Aplicativo">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin w-8 h-8 border-4 border-pink-500 border-t-transparent rounded-full"></div>
