import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { 
  ArrowLeft,
  Crown,
  Star,
  Calendar,
  DollarSign,
  User,
  CreditCard,
  Clock,
  Ban,
  CheckCircle,
  RefreshCw,
  Mail,
  Phone,
  MapPin,
  Eye
} from "lucide-react";

interface SubscriptionDetail {
  id: number;
  userId: number;
  userName: string;
  userEmail: string;
  planType: string;
  status: string;
  startDate: string;
  endDate: string;
  amount: number;
  stripeSubscriptionId: string;
  stripeCustomerId: string;
  isActive: boolean;
  cancelAtPeriodEnd: boolean;
  canceledAt?: string;
