        if (key in settings) {
          // Parse JSON or boolean values
          try {
            if (value === 'true') settings[key] = true;
            else if (value === 'false') settings[key] = false;
            else if (!isNaN(Number(value))) settings[key] = Number(value);
            else settings[key] = value;
          } catch {
            settings[key] = value;
