import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import AdminLayout from "@/components/admin/AdminLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { 
  Flag, 
  Search, 
  Filter, 
  AlertTriangle,
  User,
  MessageCircle,
  Calendar,
  Eye,
  CheckCircle,
  X
} from "lucide-react";

interface Report {
  id: number;
  reporterName: string;
  reportedName: string;
  reporterId: number;
  reportedId: number;
  reason: string;
  description: string;
  status: string;
  createdAt: string;
  resolvedAt?: string;
  category: string;
}

export default function AdminReportsNew() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");

  useEffect(() => {
    const adminToken = localStorage.getItem('adminToken');
    if (!adminToken) {
      setLocation("/admin/login");
    }
  }, [setLocation]);

  const { data: reports = [], isLoading } = useQuery({
    queryKey: ['/api/admin/reports', searchTerm, statusFilter],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchTerm) params.set('search', searchTerm);
      if (statusFilter !== 'all') params.set('status', statusFilter);

      const response = await fetch(`/api/admin/reports?${params}`);
      if (!response.ok) throw new Error('Failed to fetch reports');
      return response.json();
    }
  });

  const resolveReportMutation = useMutation({
    mutationFn: async ({ reportId, action }: { reportId: number; action: 'approve' | 'reject' }) => {
      const response = await fetch(`/api/admin/reports/${reportId}/resolve`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ action })
      });
      if (!response.ok) throw new Error('Failed to resolve report');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/reports'] });
      toast({
        title: "‚úÖ Den√∫ncia Resolvida",
        description: "A√ß√£o executada com sucesso",
      });
    },
    onError: () => {
      toast({
        title: "‚ùå Erro",
        description: "Falha ao resolver den√∫ncia",
        variant: "destructive"
      });
    }
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-500/20 text-yellow-300';
      case 'resolved':
        return 'bg-green-500/20 text-green-300';
      case 'rejected':
        return 'bg-red-500/20 text-red-300';
      default:
        return 'bg-gray-500/20 text-gray-300';
    }
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'inappropriate_content':
        return <AlertTriangle className="w-4 h-4 text-red-400" />;
      case 'harassment':
        return <User className="w-4 h-4 text-orange-400" />;
      case 'spam':
        return <MessageCircle className="w-4 h-4 text-yellow-400" />;
      default:
        return <Flag className="w-4 h-4 text-blue-400" />;
    }
  };

  if (isLoading) {
    return (
      <AdminLayout title="Gerenciar Den√∫ncias">
        <div className="flex items-center justify-center h-64 w-full">
          <div className="animate-spin w-8 h-8 border-4 border-pink-500 border-t-transparent rounded-full"></div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout title="Gerenciar Den√∫ncias">
      <div className="space-y-3 w-full max-w-full overflow-x-hidden">
        {/* Search and Filters */}
        <Card className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 w-full">
          <div className="flex flex-col sm:flex-row gap-3 w-full">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-blue-300" />
              <Input
                placeholder="Buscar den√∫ncias..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 bg-blue-700/50 border-blue-600/50 text-white text-sm w-full"
              />
            </div>
            <div className="flex gap-2">
              <Button
                variant={statusFilter === 'all' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('all')}
                className="text-xs whitespace-nowrap"
              >
                Todas
              </Button>
              <Button
                variant={statusFilter === 'pending' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('pending')}
                className="text-xs whitespace-nowrap"
              >
                Pendentes
              </Button>
              <Button
                variant={statusFilter === 'resolved' ? 'default' : 'outline'}
                size="sm"
                onClick={() => setStatusFilter('resolved')}
                className="text-xs whitespace-nowrap"
              >
                Resolvidas
              </Button>
            </div>
          </div>
        </Card>

        {/* Reports Grid */}
        <div className="grid grid-cols-1 gap-3 w-full">
          {reports.map((report: Report) => (
            <Card key={report.id} className="p-3 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 hover:bg-blue-800/70 transition-all w-full">
              {/* Report Header */}
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    {getCategoryIcon(report.category)}
                    <h3 className="text-sm font-semibold text-white truncate">
                      {report.reporterName} denunciou {report.reportedName}
                    </h3>
                  </div>
                  <p className="text-xs text-blue-200">Den√∫ncia #{report.id}</p>
                </div>
                <div className="flex flex-col items-end gap-1">
                  <Badge className={`text-xs ${getStatusColor(report.status)}`}>
                    {report.status === 'pending' ? 'Pendente' : 
                     report.status === 'resolved' ? 'Resolvida' : 'Rejeitada'}
                  </Badge>
                  <span className="text-xs text-blue-300">{formatDate(report.createdAt)}</span>
                </div>
              </div>

              {/* Report Details */}
              <div className="space-y-2 mb-3">
                <div>
                  <span className="text-xs text-blue-200">Motivo:</span>
                  <p className="text-sm text-white">{report.reason}</p>
                </div>
                {report.description && (
                  <div>
                    <span className="text-xs text-blue-200">Descri√ß√£o:</span>
                    <p className="text-sm text-white bg-blue-700/30 p-2 rounded">
                      {report.description.length > 100 
                        ? `${report.description.substring(0, 100)}...` 
                        : report.description
                      }
                    </p>
                  </div>
                )}
                {report.resolvedAt && (
                  <div className="flex items-center gap-2 text-xs text-blue-200">
                    <Calendar className="w-3 h-3" />
                    <span>Resolvida em {formatDate(report.resolvedAt)}</span>
                  </div>
                )}
              </div>

              {/* Action Buttons */}
              <div className="flex gap-2">
                <Button
                  size="sm"
                  onClick={() => setLocation(`/admin/report-details/${report.id}`)}
                  className="flex-1 bg-gradient-to-r from-pink-500 to-purple-500 hover:from-pink-600 hover:to-purple-600 text-white text-xs"
                >
                  <Eye className="w-3 h-3 mr-1" />
                  Ver Detalhes
                </Button>
                {report.status === 'pending' && (
                  <>
                    <Button
                      size="sm"
                      onClick={() => resolveReportMutation.mutate({ reportId: report.id, action: 'approve' })}
                      className="bg-green-600 hover:bg-green-700 text-white text-xs"
                    >
                      <CheckCircle className="w-3 h-3" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => resolveReportMutation.mutate({ reportId: report.id, action: 'reject' })}
                      className="border-red-500/50 text-red-400 hover:bg-red-500/20 text-xs"
                    >
                      <X className="w-3 h-3" />
                    </Button>
                  </>
                )}
              </div>
            </Card>
          ))}
        </div>

        {/* Empty State */}
        {reports.length === 0 && (
          <Card className="p-8 bg-blue-800/50 backdrop-blur-sm border-blue-700/50 text-center w-full">
            <Flag className="w-12 h-12 text-blue-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-white mb-2">Nenhuma den√∫ncia encontrada</h3>
            <p className="text-blue-200">Ajuste os filtros ou tente uma busca diferente.</p>
          </Card>
        )}
      </div>
    </AdminLayout>
  );
}

