      isLike: true,
      isSuperLike: true
    }).returning();

    // Check for mutual like to create match
    const [reverseSwipe] = await db.select().from(swipes)
      .where(and(
        eq(swipes.swiperId, swipedId),
        eq(swipes.swipedId, userId),
