import { useState, useEffect } from "react"

export function useMobile() {
  const [isMobile, setIsMobile] = useState<boolean>(false)

  useEffect(() => {
    const checkMobile = () => {
      // Verifica user agent para dispositivos m√≥veis reais
      const userAgent = navigator.userAgent || navigator.vendor;
      const isMobileDevice = /android|webos|iphone|ipad|ipod|blackberry|iemobile|opera mini/i.test(userAgent);

      // Verifica se √© um dispositivo m√≥vel real OU se √© um PWA instalado
      const isSmallScreen = window.innerWidth < 768;
      const isPWA = window.matchMedia('(display-mode: standalone)').matches;

      // S√≥ considera m√≥vel se for dispositivo m√≥vel real E (tela pequena OU PWA)
      setIsMobile(isMobileDevice && (isSmallScreen || isPWA));
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, [])

  return isMobile
}

