import { useEffect } from "react";
import { useLocation } from "wouter";
import mixLogo from "@assets/FAVICON_1752848384518_1760915867705.png";

export default function Splash() {
  const [, setLocation] = useLocation();

  useEffect(() => {
    // ‚úÖ CORRE√á√ÉO: Verificar auth imediatamente, sem delay de 5 segundos
    const checkAuthAndRedirect = async () => {
      try {
        console.log("üîµ Splash: Iniciando verifica√ß√£o de autentica√ß√£o...");

        const response = await fetch("/api/auth/user", {
          credentials: 'include'
        });

        console.log("üîµ Splash: Response status:", response.status);

        if (response.ok) {
          const user = await response.json();

          console.log("‚úÖ Splash: Usu√°rio encontrado", {
            id: user.id,
            email: user.email,
            isProfileComplete: user.isProfileComplete,
            birthDate: !!user.birthDate,
            gender: !!user.gender,
            photos: user.photos?.length,
            interestedIn: user.interestedIn?.length
          });

          // ‚úÖ CORRE√á√ÉO CR√çTICA: Se perfil completo, ir direto para discover
          if (user && user.isProfileComplete === true) {
            console.log("‚úÖ Perfil completo - redirecionando para /discover");
            setLocation("/discover");
          } else if (user && user.isProfileComplete === false) {
            console.log("‚ö†Ô∏è Perfil incompleto - redirecionando para /onboarding-flow");
            setLocation("/onboarding-flow");
          } else if (user && user.isProfileComplete === undefined) {
            console.log("‚ö†Ô∏è isProfileComplete undefined - redirecionando para /onboarding-flow");
            setLocation("/onboarding-flow");
          } else {
            // Sem usu√°rio - mostrar welcome ap√≥s 5 segundos
            console.log("‚ùå Sem usu√°rio - redirecionando para /welcome");
            setTimeout(() => setLocation("/welcome"), 5000);
          }
        } else {
          // N√£o autenticado - mostrar welcome ap√≥s 5 segundos
          console.log("‚ùå N√£o autenticado (status", response.status, ") - redirecionando para /welcome");
          setTimeout(() => setLocation("/welcome"), 5000);
        }
      } catch (error) {
        console.error("‚ùå Erro ao verificar auth na splash:", error);
        setTimeout(() => setLocation("/welcome"), 5000);
      }
    };

    checkAuthAndRedirect();
  }, [setLocation]);

  return (
    <div className="fixed inset-0 bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex items-center justify-center">
      <img 
        src={mixLogo} 
        alt="MIX" 
        className="w-48 h-auto"
        data-testid="img-splash-logo"
      />
    </div>
  );
}

