import { useState } from "react";
import { Link, useLocation } from "wouter";
import { ArrowLeft, Camera, User, Mail, Lock, Calendar, MapPin, Heart } from "lucide-react";

export function CadastreSeNew() {
  const [, setLocation] = useLocation();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    birthDate: "",
    location: "",
    interests: [] as string[],
    photos: [] as string[],
  });

  const totalSteps = 6;

  const handleNext = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
    } else {
      // Final step - create account
      console.log("Creating account:", formData);
      setLocation("/discover");
    }
  };

  const handleBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const availableInterests = [
    "M√∫sica", "Filmes", "Viagem", "Esportes", "Culin√°ria", "Arte", 
    "Leitura", "Dan√ßa", "Tecnologia", "Natureza", "Caf√©", "Fotografia"
  ];

  const toggleInterest = (interest: string) => {
    setFormData(prev => ({
      ...prev,
      interests: prev.interests.includes(interest)
        ? prev.interests.filter(i => i !== interest)
        : [...prev.interests, interest]
    }));
  };

  const renderStep = () => {
