import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle } from "lucide-react";
// import logoMix from "@assets/Logo_MIX_horizontal_1751283015324.png";

export default function CadastreSeNew() {
  const [, setLocation] = useLocation();
  const [step, setStep] = useState(1);
  const totalSteps = 6;
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: ""
  });

  const isStepValid = () => {
    switch (step) {
      case 1:
        return formData.firstName.trim().length > 0;
      case 2:
        return formData.lastName.trim().length > 0;
      case 3:
        return formData.email.trim().length > 0 && formData.email.includes('@');
      case 4:
        return formData.password.length >= 6;
      case 5:
        return formData.confirmPassword === formData.password;
      case 6:
        return true;
      default:
        return false;
    }
  };

  const handleNext = () => {
    if (step < totalSteps && isStepValid()) {
      setStep(step + 1);
    }
  };

  const handlePrevious = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");

    if (formData.password !== formData.confirmPassword) {
      setError("As senhas n√£o coincidem");
      setIsLoading(false);
      return;
    }

    try {
      const response = await fetch('/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          password: formData.password
        }),
      });

      if (response.ok) {
        setLocation('/login');
      } else {
        const data = await response.json();
        setError(data.message || "Erro ao criar conta");
      }
    } catch (err) {
      setError("Erro de conex√£o");
    } finally {
      setIsLoading(false);
    }
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <div className="space-y-6">
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-gray-800 mb-3">Como voc√™ gostaria de ser chamado?</h2>
              <p className="text-gray-600 text-base">Vamos come√ßar com seu primeiro nome</p>
            </div>
            <div className="space-y-4">
              <Input
                type="text"
                placeholder="Digite seu primeiro nome"
                value={formData.firstName}
                onChange={(e) => setFormData({...formData, firstName: e.target.value})}
                className="h-16 text-xl border-2 border-gray-300 focus:border-pink-500 rounded-2xl px-6 text-center font-medium"
                required
                autoFocus
              />
            </div>
            <div className="text-center">
              <p className="text-sm text-gray-500">Passo 1 de 6</p>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-6">
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-gray-800 mb-3">Oi, {formData.firstName}! üëã</h2>
              <p className="text-gray-600 text-base">Agora me conta seu sobrenome</p>
            </div>
            <div className="space-y-4">
              <Input
                type="text"
                placeholder="Digite seu sobrenome"
                value={formData.lastName}
                onChange={(e) => setFormData({...formData, lastName: e.target.value})}
                className="h-16 text-xl border-2 border-gray-300 focus:border-pink-500 rounded-2xl px-6 text-center font-medium"
                required
                autoFocus
              />
            </div>
            <div className="text-center">
              <p className="text-sm text-gray-500">Passo 2 de 6</p>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="space-y-6">
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-gray-800 mb-3">Qual √© o seu email?</h2>
              <p className="text-gray-600 text-base">Usaremos para verificar sua conta e manter contato</p>
            </div>
            <div className="space-y-4">
              <Input
                type="email"
