          </Alert>
        )}

        <form onSubmit={step === 6 ? handleSignup : (e) => { e.preventDefault(); handleNext(); }}>
          {renderStep()}

          {/* Bot√µes de navega√ß√£o */}
          <div className="flex gap-4 mt-10">
            {step > 1 && (
