              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full bg-gray-900 text-white rounded-lg pl-12 pr-4 py-3 focus:outline-none focus:ring-2 focus:ring-pink-500 border-none"
              data-testid="input-search-matches"
            />
          </div>
        </div>

        {/* Se√ß√£o: Novos Matches */}
        {newMatches.length > 0 && (
          <div className="mb-6 px-4">
            <div className="flex items-center justify-between mb-3">
              <h2 className="text-white font-bold text-lg flex items-center gap-2">
                Novos Matches 
                {newMatches.length > 0 && (
                  <span className="bg-red-500 text-white text-xs font-bold px-2 py-0.5 rounded-full">
                    {newMatches.length}
                  </span>
                )}
              </h2>
            </div>

            {/* Carrossel horizontal de novos matches */}
            <div className="flex gap-4 overflow-x-auto pb-2 scrollbar-hide">
              {/* Card "+99" se tiver muitos matches */}
              {newMatches.length > 5 && (
                <button
                  onClick={() => setLocation('/discover')}
                  className="flex-shrink-0"
                  data-testid="button-see-all-matches"
                >
                  <div className="relative">
                    <div className="w-20 h-20 rounded-2xl bg-gradient-to-br from-yellow-400 to-orange-500 border-4 border-yellow-500 flex items-center justify-center">
                      <span className="text-2xl font-bold text-white">+{newMatches.length - 4}</span>
                    </div>
                    <div className="text-center mt-2">
                      <p className="text-white text-sm font-medium">Curtidas</p>
                    </div>
                  </div>
                </button>
              )}

              {/* Fotos dos novos matches */}
              {newMatches.slice(0, newMatches.length > 5 ? 4 : 10).map((conv) => (
                <button
                  key={conv.match.id}
                  onClick={() => openMatchProfile(conv.profile.userId)}
                  className="flex-shrink-0"
                  data-testid={`new-match-${conv.match.id}`}
                >
                  <div className="relative">
                    <div className="w-20 h-20 rounded-2xl overflow-hidden border-4 border-pink-500">
                      <img
                        src={conv.profile.photos?.[0] || `https://ui-avatars.com/api/?name=${conv.profile.name}&background=ec4899&color=fff&size=100`}
                        alt={conv.profile.name}
                        className="w-full h-full object-cover"
                      />
                    </div>
                    {/* Badge azul verificado */}
                    {conv.profile.isVerified && (
                      <div className="absolute bottom-1 right-1">
                        <VerifiedBadge className="w-5 h-5" />
                      </div>
                    )}
                  </div>
                  <div className="text-center mt-2">
                    <p className="text-white text-sm font-medium truncate w-20">
                      {conv.profile.name.split(' ')[0]}
                    </p>
                  </div>
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Se√ß√£o: Mensagens */}
        <div className="px-4">
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-white font-bold text-lg flex items-center gap-2">
              Mensagens
              {unreadMessagesCount > 0 && (
                <span className="bg-red-500 text-white text-xs font-bold px-2 py-0.5 rounded-full">
                  {unreadMessagesCount}
                </span>
              )}
            </h2>
          </div>

          {/* Lista de conversas */}
          {conversations.length === 0 && newMatches.length === 0 ? (
            // Estado vazio - quando n√£o h√° matches nem conversas
            <div className="flex flex-col items-center justify-center py-20 text-center">
              <div className="mb-8 relative">
                <svg width="200" height="200" viewBox="0 0 200 200" className="mx-auto">
                  <rect x="40" y="50" width="120" height="160" rx="12" fill="#2D2D2D" transform="rotate(-5 100 130)" />
                  <rect x="40" y="40" width="120" height="160" rx="12" fill="#1F2937" stroke="#10B981" strokeWidth="3" />
                  <text x="100" y="120" textAnchor="middle" fill="#10B981" fontSize="24" fontWeight="bold" fontFamily="Arial">
                    LIKE
                  </text>
                  <rect x="40" y="40" width="120" height="160" rx="12" fill="url(#glow)" opacity="0.3" />
                  <defs>
                    <linearGradient id="glow" x1="0%" y1="0%" x2="0%" y2="100%">
                      <stop offset="0%" stopColor="#10B981" stopOpacity="0.5" />
                      <stop offset="100%" stopColor="#10B981" stopOpacity="0" />
                    </linearGradient>
                  </defs>
                </svg>
              </div>

              <h2 className="text-white text-2xl font-bold mb-4">
                Comece a deslizar
              </h2>
              <p className="text-gray-400 text-base leading-relaxed max-w-sm px-4">
                Quando voc√™ der um Match com outros usu√°rios, eles aparecer√£o aqui para que voc√™ possa enviar mensagens
              </p>
            </div>
          ) : conversations.length === 0 ? (
            // Tem novos matches mas nenhuma conversa iniciada
            <div className="text-center py-10">
              <p className="text-gray-400 text-sm">
                Diga ol√° para seus novos matches! üëã
              </p>
            </div>
          ) : (
            // Lista de conversas ativas
            <div className="space-y-0 divide-y divide-gray-800">
              {conversations
                .filter(conv => 
                  searchQuery === "" || 
                  conv.profile.name.toLowerCase().includes(searchQuery.toLowerCase())
                )
                .map((conversation) => {
                  // isOnline vir√° do backend quando implementado
                  const isOnline = false;

                  return (
                    <button
                      key={conversation.match.id}
                      onClick={() => openConversation(conversation.match.id)}
                      className="w-full p-4 hover:bg-gray-900/50 transition-colors text-left"
                      data-testid={`conversation-${conversation.match.id}`}
                    >
                      <div className="flex items-center gap-3">
                        {/* Foto com indicador online */}
                        <div className="relative flex-shrink-0">
                          <div className="w-16 h-16 rounded-full overflow-hidden">
                            <img
                              src={conversation.profile.photos?.[0] || `https://ui-avatars.com/api/?name=${conversation.profile.name}&background=ec4899&color=fff&size=100`}
                              alt={conversation.profile.name}
                              className="w-full h-full object-cover"
                            />
                          </div>
                          {/* Bolinha verde de online */}
                          {isOnline && (
                            <div className="absolute bottom-0 right-0 w-5 h-5 bg-green-500 border-4 border-black rounded-full"></div>
                          )}
                        </div>

                        {/* Informa√ß√µes da conversa */}
                        <div className="flex-1 min-w-0">
                          {/* Nome + Badge "CURTIU VOC√ä" */}
                          <div className="flex items-center gap-2 mb-1">
                            <h3 className="font-semibold text-white text-base">
                              {conversation.profile.name.split(' ')[0]}
                            </h3>
                            {conversation.profile.isVerified && (
                              <VerifiedBadge className="w-4 h-4" />
                            )}
                            {/* Badge "CURTIU VOC√ä" - quando a pessoa curtiu voc√™ primeiro */}
                            {conversation.match.user2Id === conversation.profile.userId && (
                              <span className="bg-yellow-500 text-black text-xs font-bold px-2 py-0.5 rounded">
                                CURTIU VOC√ä
                              </span>
                            )}
                          </div>

                          {/* Preview da mensagem */}
                          <div className="flex items-center justify-between gap-2">
                            <p className="text-gray-400 text-sm truncate">
                              {conversation.lastMessage?.content || "Diga ol√°! üëã"}
                            </p>
                            {/* Badge "Sua vez" - se a √∫ltima mensagem foi do match */}
                            {conversation.lastMessage && 
                             conversation.lastMessage.senderId === conversation.profile.userId && (
                              <span className="bg-white text-black text-xs font-semibold px-2 py-0.5 rounded flex-shrink-0">
                                Sua vez
                              </span>
                            )}
                          </div>
                        </div>
                      </div>
                    </button>
                  );
                })}
            </div>
          )}
        </div>
      </div>

      <BottomNavigation />

      <style>{`
        .scrollbar-hide::-webkit-scrollbar {
          display: none;
        }
        .scrollbar-hide {
          -ms-overflow-style: none;
          scrollbar-width: none;
        }
      `}</style>
    </div>
  );
}

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp, jsonb } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  resetToken: text("reset_token"),
  resetTokenExpiry: timestamp("reset_token_expiry"),
  isOnline: boolean("is_online").default(false).notNull(),
  lastSeen: timestamp("last_seen").defaultNow(),
  subscriptionType: text("subscription_type").default("free").notNull(), // free, premium, vip
  dailyLikes: integer("daily_likes").default(0).notNull(), // contador de likes di√°rios
  lastLikeReset: timestamp("last_like_reset").defaultNow(), // data do √∫ltimo reset
  stripeCustomerId: text("stripe_customer_id"), // ID do cliente no Stripe
  stripeSubscriptionId: text("stripe_subscription_id"), // ID da assinatura no Stripe
  firstName: text("first_name").default("").notNull(),
  lastName: text("last_name").default("").notNull(),
  profileImage: text("profile_image"),
  phone: text("phone"),
  birthDate: text("birth_date"),
  gender: text("gender"),
  sexualOrientation: text("sexual_orientation").array().default([]),
  interestedIn: text("interested_in").array().default([]),
  city: text("city"),
  location: text("location"),
  education: text("education"),
  company: text("company"),
  school: text("school"),
  interests: text("interests").array().default([]),
  bio: text("bio"),
  photos: text("photos").array().default([]),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const profiles = pgTable("profiles", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  name: text("name").notNull(),
  age: integer("age").notNull(),
  bio: text("bio"),
  profession: text("profession"),
  photos: text("photos").array().notNull().default([]),
  location: text("location"),
  interests: text("interests").array().default([]),

  // Mix-style fields
  job: text("job"),
  company: text("company"),
  school: text("school"),
  height: integer("height"), // em cent√≠metros
  relationshipGoals: text("relationship_goals"), // "long-term", "short-term", "figuring-out", "friends"
  languages: text("languages").array().default([]),
  starSign: text("star_sign"),
  educationLevel: text("education_level"),
  familyPlans: text("family_plans"), // "want-children", "dont-want", "open-to", "have-children"
  personalityType: text("personality_type"), // MBTI types
  communicationStyle: text("communication_style"),
  loveStyle: text("love_style"),
  instagram: text("instagram"),
  spotify: text("spotify"),

  // Lifestyle fields
  pronouns: text("pronouns"),
  pets: text("pets"),
  drinking: text("drinking"),
  smoking: text("smoking"),
  exercise: text("exercise"),
  diet: text("diet"),
  sleepSchedule: text("sleep_schedule"),

  // Settings
  maxDistance: integer("max_distance").default(50),
  ageRangeMin: integer("age_range_min").default(18),
  ageRangeMax: integer("age_range_max").default(99),
  isActive: boolean("is_active").default(true),
  isVerified: boolean("is_verified").default(false),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const swipes = pgTable("swipes", {
  id: serial("id").primaryKey(),
  swiperId: integer("swiper_id").references(() => users.id).notNull(),
  swipedId: integer("swiped_id").references(() => users.id).notNull(),
  isLike: boolean("is_like").notNull(),
  isSuperLike: boolean("is_super_like").default(false),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const matches = pgTable("matches", {
  id: serial("id").primaryKey(),
  user1Id: integer("user1_id").references(() => users.id).notNull(),
  user2Id: integer("user2_id").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const messages = pgTable("messages", {
  id: serial("id").primaryKey(),
  matchId: integer("match_id").references(() => matches.id).notNull(),
  senderId: integer("sender_id").references(() => users.id).notNull(),
  content: text("content").notNull(),
  imageUrl: text("image_url"),
  isRead: boolean("is_read").default(false),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de planos de assinatura
export const subscriptionPlans = pgTable("subscription_plans", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(), // "Premium", "VIP"
  description: text("description"), // Descri√ß√£o do plano
  stripePriceId: text("stripe_price_id").notNull(), // ID do price no Stripe
  price: integer("price").notNull(), // Pre√ßo em centavos
  currency: text("currency").default("brl").notNull(),
  interval: text("interval").notNull(), // "month", "year"
  features: jsonb("features").notNull(), // JSON com features do plano
  paymentMethods: text("payment_methods").array().default(['card', 'pix']), // Formas de pagamento aceitas
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de assinaturas
export const subscriptions = pgTable("subscriptions", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  planId: integer("plan_id").references(() => subscriptionPlans.id).notNull(),
  stripeSubscriptionId: text("stripe_subscription_id").notNull().unique(),
  stripeCustomerId: text("stripe_customer_id").notNull(),
  status: text("status").notNull(), // "active", "canceled", "past_due", etc
  currentPeriodStart: timestamp("current_period_start").notNull(),
  currentPeriodEnd: timestamp("current_period_end").notNull(),
  cancelAtPeriodEnd: boolean("cancel_at_period_end").default(false),
  canceledAt: timestamp("canceled_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Tabela de pagamentos
export const payments = pgTable("payments", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  subscriptionId: integer("subscription_id").references(() => subscriptions.id),
  stripePaymentIntentId: text("stripe_payment_intent_id").notNull().unique(),
  amount: integer("amount").notNull(), // Em centavos
  currency: text("currency").default("brl").notNull(),
  status: text("status").notNull(), // "succeeded", "failed", "pending"
  paymentMethod: text("payment_method"), // "card", "pix", etc
  description: text("description"),
  metadata: jsonb("metadata"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const favorites = pgTable("favorites", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  profileId: integer("profile_id").references(() => profiles.id).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de check-ins
export const checkIns = pgTable("check_ins", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  establishmentName: text("establishment_name").notNull(),
  establishmentType: text("establishment_type"), // Bar, Restaurante, Caf√©, etc
  city: text("city").default("S√£o Paulo").notNull(),
  address: text("address"),
  latitude: text("latitude"),
  longitude: text("longitude"),
  qrCodeData: text("qr_code_data"), // Dados originais do QR Code
  checkInMethod: text("check_in_method").notNull(), // "qr", "manual", "establishment"
  isActive: boolean("is_active").default(true), // Check-in ativo ou j√° expirou
  expiresAt: timestamp("expires_at"), // Check-in expira ap√≥s X horas
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de estabelecimentos parceiros (futura integra√ß√£o)
export const establishments = pgTable("establishments", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  type: text("type").notNull(), // Bar, Restaurante, Caf√©, etc
  neighborhood: text("neighborhood"),
  city: text("city").default("S√£o Paulo").notNull(),
  address: text("address"),
  latitude: text("latitude"),
  longitude: text("longitude"),
  qrCodeData: text("qr_code_data"), // QR Code √∫nico do estabelecimento
  isPremium: boolean("is_premium").default(false), // Estabelecimento premium/parceiro
  isActive: boolean("is_active").default(true),
  images: text("images").array().default([]),
  description: text("description"),
  contactPhone: text("contact_phone"),
  contactEmail: text("contact_email"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de boosts (impulsos de perfil)
export const boosts = pgTable("boosts", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  isActive: boolean("is_active").default(true),
  expiresAt: timestamp("expires_at").notNull(), // Boost dura 30 minutos
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de rewinds (voltar √∫ltima a√ß√£o)
export const rewinds = pgTable("rewinds", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  swipeId: integer("swipe_id").references(() => swipes.id).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de verifica√ß√µes de perfil
export const verifications = pgTable("verifications", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull().unique(),
  isVerified: boolean("is_verified").default(false),
  verificationMethod: text("verification_method"), // "selfie", "document", "manual"
  verifiedAt: timestamp("verified_at"),
  verificationImages: text("verification_images").array().default([]),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de visualiza√ß√µes de perfil
export const profileViews = pgTable("profile_views", {
  id: serial("id").primaryKey(),
  viewedUserId: integer("viewed_user_id").references(() => users.id).notNull(),
  viewerUserId: integer("viewer_user_id").references(() => users.id).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de den√∫ncias (reports)
export const reports = pgTable("reports", {
  id: serial("id").primaryKey(),
  reporterId: integer("reporter_id").references(() => users.id).notNull(),
  reportedUserId: integer("reported_user_id").references(() => users.id).notNull(),
  reason: text("reason").notNull(), // harassment, fake_profile, inappropriate_content, spam, other
  description: text("description"),
  status: text("status").default("pending").notNull(), // pending, reviewing, resolved, dismissed
  action: text("action"), // banned, warned, no_action
  reviewedBy: integer("reviewed_by").references(() => users.id),
  reviewedAt: timestamp("reviewed_at"),
  notes: text("notes"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de configura√ß√µes do app
export const appSettings = pgTable("app_settings", {
  id: serial("id").primaryKey(),
  key: text("key").notNull().unique(),
  value: text("value").notNull(),
  type: text("type").default("string").notNull(), // string, number, boolean, json
  category: text("category").notNull(), // general, limits, security, features, notifications
  description: text("description"),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Tabela de usu√°rios admin
export const adminUsers = pgTable("admin_users", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull().unique(),
  role: text("role").default("moderator").notNull(), // super_admin, admin, moderator
  permissions: text("permissions").array().default([]), // users, matches, messages, reports, settings, analytics
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de notifica√ß√µes push
export const notifications = pgTable("notifications", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id),
  title: text("title").notNull(),
  body: text("body").notNull(),
  type: text("type").notNull(), // match, message, like, super_like, boost, system
  data: jsonb("data"),
  isRead: boolean("is_read").default(false),
  isSent: boolean("is_sent").default(false),
  sentAt: timestamp("sent_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
});

// Schema simplificado para registro com email e password
export const registerUserSchema = z.object({
  email: z.string().email("Email inv√°lido"),
  password: z.string().min(6, "Senha deve ter pelo menos 6 caracteres"),
});

export const insertProfileSchema = createInsertSchema(profiles).omit({
  id: true,
  userId: true,
  createdAt: true,
});

export const insertSwipeSchema = createInsertSchema(swipes).omit({
  id: true,
  createdAt: true,
});

export const insertMessageSchema = createInsertSchema(messages).omit({
  id: true,
  createdAt: true,
});

export const insertSubscriptionPlanSchema = createInsertSchema(subscriptionPlans).omit({
  id: true,
  createdAt: true,
});

export const insertSubscriptionSchema = createInsertSchema(subscriptions).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPaymentSchema = createInsertSchema(payments).omit({
  id: true,
  createdAt: true,
});

export const insertCheckInSchema = createInsertSchema(checkIns).omit({
  id: true,
  createdAt: true,
});

export const insertEstablishmentSchema = createInsertSchema(establishments).omit({
  id: true,
  createdAt: true,
});

export const insertBoostSchema = createInsertSchema(boosts).omit({
  id: true,
  createdAt: true,
});

export const insertRewindSchema = createInsertSchema(rewinds).omit({
  id: true,
  createdAt: true,
});

export const insertVerificationSchema = createInsertSchema(verifications).omit({
  id: true,
  createdAt: true,
});

export const insertReportSchema = createInsertSchema(reports).omit({
  id: true,
  createdAt: true,
});

export const insertAppSettingSchema = createInsertSchema(appSettings).omit({
  id: true,
  updatedAt: true,
});

export const insertAdminUserSchema = createInsertSchema(adminUsers).omit({
  id: true,
  createdAt: true,
});

export const insertNotificationSchema = createInsertSchema(notifications).omit({
  id: true,
  createdAt: true,
});

// Relations
export const usersRelations = relations(users, ({ one, many }) => ({
  profile: one(profiles, {
    fields: [users.id],
    references: [profiles.userId],
  }),
  swipesGiven: many(swipes, {
    relationName: "swiper",
  }),
  swipesReceived: many(swipes, {
    relationName: "swiped",
  }),
  matchesAsUser1: many(matches, {
    relationName: "user1",
  }),
  matchesAsUser2: many(matches, {
    relationName: "user2",
  }),
  messagesSent: many(messages, {
    relationName: "sender",
  }),
}));

export const profilesRelations = relations(profiles, ({ one }) => ({
  user: one(users, {
    fields: [profiles.userId],
    references: [users.id],
  }),
}));

export const swipesRelations = relations(swipes, ({ one }) => ({
  swiper: one(users, {
    fields: [swipes.swiperId],
    references: [users.id],
    relationName: "swiper",
  }),
  swiped: one(users, {
    fields: [swipes.swipedId],
    references: [users.id],
    relationName: "swiped",
  }),
}));

export const matchesRelations = relations(matches, ({ one, many }) => ({
  user1: one(users, {
    fields: [matches.user1Id],
    references: [users.id],
    relationName: "user1",
  }),
  user2: one(users, {
    fields: [matches.user2Id],
    references: [users.id],
    relationName: "user2",
  }),
  messages: many(messages),
}));

export const messagesRelations = relations(messages, ({ one }) => ({
  match: one(matches, {
    fields: [messages.matchId],
    references: [matches.id],
  }),
  sender: one(users, {
    fields: [messages.senderId],
    references: [users.id],
    relationName: "sender",
  }),
}));

export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Profile = typeof profiles.$inferSelect;
export type InsertProfile = z.infer<typeof insertProfileSchema>;
export type Swipe = typeof swipes.$inferSelect;
export type InsertSwipe = z.infer<typeof insertSwipeSchema>;
export type Match = typeof matches.$inferSelect;
export type Message = typeof messages.$inferSelect;
export type InsertMessage = z.infer<typeof insertMessageSchema>;

export type SubscriptionPlan = typeof subscriptionPlans.$inferSelect;
