import { useEffect, useState, useCallback } from "react";
import { useQuery } from "@tanstack/react-query";
import { Settings, ChevronRight, Star, Zap, Crown, Check, Lock, Shield } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import BottomNavigation from "@/components/BottomNavigation";
import mixLogo from "@assets/Logo_MIX_horizontal_1752607947932.png";
import useEmblaCarousel from 'embla-carousel-react';

interface Profile {
  id: number;
  userId: number;
  name: string;
  age: number;
  location?: string;
  bio?: string;
  profession?: string;
  interests?: string[];
  photos?: string[];
  isVerified?: boolean;
  job?: string;
  company?: string;
  school?: string;
  height?: number;
  relationshipGoals?: string;
  languages?: string[];
  starSign?: string;
  educationLevel?: string;
  familyPlans?: string;
  personalityType?: string;
  communicationStyle?: string;
  loveStyle?: string;
  instagram?: string;
  spotify?: string;
}

export default function Profile() {
  const [, setLocation] = useLocation();
  const { user, isAuthenticated, isLoading } = useAuth();
  const { toast } = useToast();
  const [emblaRef, emblaApi] = useEmblaCarousel({ loop: false, align: 'start' });
  const [selectedIndex, setSelectedIndex] = useState(0);

  const onSelect = useCallback(() => {
    if (!emblaApi) return;
    setSelectedIndex(emblaApi.selectedScrollSnap());
  }, [emblaApi]);

  useEffect(() => {
    if (!emblaApi) return;
    onSelect();
    emblaApi.on('select', onSelect);
    return () => {
      emblaApi.off('select', onSelect);
    };
  }, [emblaApi, onSelect]);

  const { data: profile, isLoading: profileLoading, error } = useQuery<Profile>({
    queryKey: ['/api/profiles', user?.id],
    enabled: !!user?.id,
    retry: false,
    queryFn: async () => {
      const res = await fetch(`/api/profiles/${user?.id}`, {
        credentials: 'include',
      });
      if (!res.ok) {
        if (res.status === 404) {
          throw new Error('Profile not found');
        }
        throw new Error('Profile not found');
      }
      return res.json();
    }
  });

  const { data: superLikes } = useQuery<{ count: number; dailyLimit: number; remaining: number }>({
    queryKey: ['/api/super-likes'],
    enabled: !!user?.id,
    retry: false,
  });

  const { data: boostStats } = useQuery<{ views: number; likes: number; matches: number; available?: number }>({
    queryKey: ['/api/boost'],
    enabled: !!user?.id,
    retry: false,
  });

  const { data: subscription } = useQuery<{ planName: string; status: string; expiresAt: string } | null>({
    queryKey: ['/api/subscription/current'],
    enabled: !!user?.id,
    retry: false,
  });

  const { data: profileViews } = useQuery<{ views: number }>({
    queryKey: ['/api/profile/views'],
    enabled: !!user?.id,
    retry: false,
  });

  const handleLogout = async () => {
    try {
      const res = await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include',
      });
      if (res.ok) {
        toast({ title: "Desconectado!", description: "At√© logo!" });
        window.location.href = '/';
      }
    } catch (error) {
      toast({ title: "Erro", description: "N√£o foi poss√≠vel sair", variant: "destructive" });
    }
  };

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      setLocation('/login');
    }
  }, [isLoading, isAuthenticated, setLocation]);

  if (isLoading || profileLoading) {
    return (
