        {/* Bot√£o invis√≠vel para fechar (apenas para desenvolvimento) */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-white/50 hover:text-white text-2xl"
        >
          √ó
        </button>
      </div>
    </div>
  );
}

