import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertCircle, CheckCircle, XCircle } from "lucide-react";

export default function OAuthTest() {
  const [logs, setLogs] = useState<string[]>([]);
  const [sessionInfo, setSessionInfo] = useState<any>(null);

  useEffect(() => {
    // Check session on load
    checkSession();
  }, []);

  const addLog = (message: string) => {
    setLogs(prev => [...prev, `${new Date().toLocaleTimeString()}: ${message}`]);
  };

  const checkSession = async () => {
    try {
      const response = await fetch('/api/user', {
        credentials: 'include'
      });
      const data = await response.json();
      setSessionInfo(data);
      addLog(`Sess√£o atual: ${response.ok ? 'Autenticado' : 'N√£o autenticado'}`);
    } catch (error) {
      addLog(`Erro ao verificar sess√£o: ${error}`);
    }
  };

  const testGoogleOAuth = () => {
    addLog('Iniciando teste de Google OAuth...');
    addLog('Redirecionando para /api/auth/google');
    window.location.href = '/api/auth/google';
  };

  const testCallback = async () => {
    try {
      addLog('Testando callback diretamente...');
      const response = await fetch('/api/auth/google/callback', {
        credentials: 'include',
        redirect: 'manual'
      });
      addLog(`Resposta do callback: ${response.status}`);
    } catch (error) {
      addLog(`Erro no callback: ${error}`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        <Card>
          <CardHeader>
            <CardTitle>Teste de Google OAuth</CardTitle>
            <CardDescription>
              Diagn√≥stico completo do fluxo de autentica√ß√£o
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex gap-4">
              <Button onClick={testGoogleOAuth} data-testid="button-test-google">
                Testar Login Google
              </Button>
              <Button onClick={checkSession} variant="outline" data-testid="button-check-session">
                Verificar Sess√£o
              </Button>
            </div>

            {sessionInfo && (
              <div className="p-4 bg-gray-50 rounded-lg">
                <h3 className="font-semibold mb-2">Estado da Sess√£o:</h3>
                <pre className="text-xs overflow-auto">
                  {JSON.stringify(sessionInfo, null, 2)}
                </pre>
              </div>
            )}

            <div className="space-y-2">
              <h3 className="font-semibold">Logs:</h3>
              <div className="bg-black text-green-400 p-4 rounded-lg h-64 overflow-auto font-mono text-sm">
                {logs.length === 0 ? (
                  <p>Nenhum log ainda...</p>
                ) : (
                  logs.map((log, i) => (
                    <div key={i}>{log}</div>
                  ))
                )}
              </div>
            </div>

            <div className="space-y-2">
              <h3 className="font-semibold">URLs de Callback:</h3>
              <div className="p-4 bg-gray-50 rounded-lg text-sm">
                <p className="mb-2">
                  <strong>Callback URL atual:</strong>
                </p>
                <code className="block p-2 bg-white rounded border break-all">
                  {window.location.origin}/api/auth/google/callback
                </code>
                <p className="mt-4 text-red-600">
                  ‚ö†Ô∏è Esta URL precisa estar configurada no Google Cloud Console
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

