interface MatchModalProps {
  isOpen: boolean;
  onClose: () => void;
  matchProfile: {
    id: number;
    name: string;
    age?: number;
    photos?: string[];
    bio?: string;
  };
  matchId?: number;
}

export function MatchModal({ isOpen, onClose, matchProfile, matchId }: MatchModalProps) {
  const [, setLocation] = useLocation();
