import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { ChevronLeft, MessageSquare, GraduationCap, Sparkles } from "lucide-react";

export default function OnboardingPersonality() {
  const [, setLocation] = useLocation();
  const [currentPage, setCurrentPage] = useState(0);
  const [answers, setAnswers] = useState({
    communication: [] as string[],
    education: [] as string[],
    sign: "",
    loveLanguage: [] as string[]
  });

  const handleContinue = () => {
    if (currentPage < 3) {
      setCurrentPage(currentPage + 1);
    } else {
      localStorage.setItem("onboarding_personality", JSON.stringify(answers));
      setLocation("/onboarding/interests");
    }
  };

  const toggleAnswer = (category: keyof typeof answers, value: string) => {
    setAnswers(prev => {
      const current = prev[category];
      if (Array.isArray(current)) {
        return {
          ...prev,
          [category]: current.includes(value) 
            ? current.filter(v => v !== value)
            : [...current, value]
        };
      }
      return { ...prev, [category]: value };
    });
  };

  const pages = [
    {
      title: "O que faz de voc√™ uma pessoa √∫nica?",
      subtitle: "N√£o guarde segredo. Autenticidade atrai autenticidade.",
      icon: <MessageSquare className="w-6 h-6 text-white" />,
      question: "Qual √© o seu estilo de comunica√ß√£o?",
      options: [
        "Fico no WhatsApp o dia todo",
        "Fico o dia inteiro no Whats",
        "Gosto de falar no telefone",
        "Adoro chamadas de v√≠deo",
        "Demoro pra responder no WhatsApp",
        "Odeio falar por mensagem",
        "Melhor falar pessoalmente"
      ],
      category: "communication" as const
    },
    {
      title: "O que faz de voc√™ uma pessoa √∫nica?",
      subtitle: "N√£o guarde segredo. Autenticidade atrai autenticidade.",
      icon: <GraduationCap className="w-6 h-6 text-white" />,
      question: "Qual √© o seu n√≠vel de escolaridade?",
      options: [
        "Superior completo",
        "Fazendo faculdade",
        "Cursando o Ensino M√©dio",
        "Doutorado completo",
        "Fazendo p√≥s",
        "Mestrado completo",
        "Curso t√©cnico"
      ],
      category: "education" as const
    },
    {
      title: "O que faz de voc√™ uma pessoa √∫nica?",
      subtitle: "N√£o guarde segredo. Autenticidade atrai autenticidade.",
      icon: <Sparkles className="w-6 h-6 text-white" />,
      question: "Qual √© o seu signo?",
      options: [
        "Capric√≥rnio", "Aqu√°rio", "Peixes", "√Åries",
        "Touro", "G√™meos", "C√¢ncer", "Le√£o",
        "Virgem", "Libra", "Escorpi√£o", "Sagit√°rio"
      ],
      category: "sign" as const,
      singleSelect: true
    },
    {
      title: "O que faz de voc√™ uma pessoa √∫nica?",
      subtitle: "N√£o guarde segredo. Autenticidade atrai autenticidade.",
      icon: <MessageSquare className="w-6 h-6 text-white" />,
      question: "Como voc√™ recebe amor?",
      options: [
        "Gestos de servi√ßo",
        "Presentes",
        "Toque f√≠sico",
        "Elogios",
        "Tempo de qualidade"
      ],
      category: "loveLanguage" as const
    }
  ];

  const currentPageData = pages[currentPage];
  const currentAnswer = answers[currentPageData.category];
  const hasAnswer = Array.isArray(currentAnswer) ? currentAnswer.length > 0 : !!currentAnswer;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 flex flex-col">
      {/* Progress bar */}
      <div className="h-1 bg-gray-700">
        <div className="h-full bg-gradient-to-r from-pink-500 to-pink-600" style={{ width: `${75 + (currentPage * 2.5)}%` }}></div>
      </div>

      {/* Header */}
      <div className="p-4 flex items-center justify-between">
        <button 
          onClick={() => currentPage > 0 ? setCurrentPage(currentPage - 1) : setLocation("/onboarding/distance")}
          data-testid="button-back"
        >
          <ChevronLeft className="w-8 h-8 text-white" />
        </button>
        <button 
          onClick={() => setLocation("/onboarding/interests")} 
          className="text-gray-500 text-sm"
          data-testid="button-skip"
        >
          Pular
        </button>
      </div>

      {/* Content */}
      <div className="flex-1 flex flex-col justify-between p-6">
        <div>
          <h1 className="text-white text-3xl font-bold mb-2">
            {currentPageData.title}
          </h1>
          <p className="text-gray-400 text-sm mb-8">
            {currentPageData.subtitle}
          </p>

          <div className="flex items-center gap-2 mb-4">
            {currentPageData.icon}
            <h2 className="text-white font-semibold">{currentPageData.question}</h2>
          </div>

          <div className="space-y-2 flex flex-wrap gap-2">
            {currentPageData.options.map((option) => {
              const isSelected = Array.isArray(currentAnswer) 
                ? currentAnswer.includes(option)
                : currentAnswer === option;

              return (
                <button
                  key={option}
                  onClick={() => toggleAnswer(currentPageData.category, option)}
                  className={`px-4 py-2 rounded-full border transition-all ${
                    isSelected
                      ? "border-pink-500 bg-pink-500/20 text-white"
                      : "border-gray-600 bg-transparent text-gray-300"
                  }`}
                  data-testid={`button-option-${option}`}
                >
                  {option}
                </button>
              );
            })}
          </div>
        </div>

        <div className="space-y-3">
          <div className="text-center text-sm text-gray-500">
            Pr√≥xima {currentPage}/4
          </div>
          <Button
            onClick={handleContinue}
            disabled={!hasAnswer}
            className="w-full bg-gray-600 hover:bg-gray-700 disabled:bg-gray-800 disabled:text-gray-600 text-white font-bold py-6 rounded-full text-lg"
            data-testid="button-continue"
          >
            Pr√≥xima
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/pages/match-notification.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";

interface MatchNotificationProps {
  currentUserPhoto?: string;
  matchedUserPhoto?: string;
  matchedUserName?: string;
  matchId?: number;
  onClose: () => void;
}

export default function MatchNotification({
  currentUserPhoto = "",
  matchedUserPhoto = "",
  matchedUserName = "Usu√°rio",
  matchId,
  onClose
}: MatchNotificationProps) {
  const [, setLocation] = useLocation();
  const [message, setMessage] = useState("");
  const [isSending, setIsSending] = useState(false);
  const { toast } = useToast();

  const quickMessages = ["üëã", "üòä", "‚ù§Ô∏è", "üòç"];

  const handleSendMessage = async (text: string) => {
    if (!text.trim() || !matchId) return;

    setIsSending(true);
    try {
      const response = await fetch(`/api/matches/${matchId}/messages`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ content: text }),
      });

      if (response.ok) {
        toast({
          title: "Mensagem enviada!",
          description: "Comece a conversa agora",
        });
        setMessage("");
        // Redirecionar para a conversa
        setTimeout(() => {
          setLocation(`/messages/${matchId}`);
        }, 500);
      } else {
        throw new Error("Erro ao enviar mensagem");
      }
    } catch (error) {
      toast({
        title: "Erro ao enviar",
        description: "Tente novamente",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  const handleQuickMessage = (emoji: string) => {
    handleSendMessage(emoji);
  };

  const handleKeepSwiping = () => {
    onClose();
  };

  return (
    <div className="fixed inset-0 z-50 bg-gradient-to-b from-green-400 via-green-500 to-green-600 flex flex-col items-center justify-between p-6 py-8 animate-in fade-in duration-500">
      {/* Bot√£o Fechar */}
      <button
        onClick={handleKeepSwiping}
        className="self-start text-white hover:text-white/80 transition-colors"
        data-testid="button-close-match"
      >
        <X className="w-8 h-8" />
      </button>

      {/* Conte√∫do Central */}
      <div className="flex-1 flex flex-col items-center justify-center w-full max-w-md">
        {/* Cora√ß√µes decorativos no fundo */}
        <div className="absolute top-1/4 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <div className="relative">
            {/* Cora√ß√£o grande externo */}
            <svg
              width="400"
              height="400"
              viewBox="0 0 400 400"
              className="opacity-30"
            >
              <path
                d="M200,350 C200,350 50,250 50,150 C50,100 75,75 100,75 C125,75 150,90 175,120 C175,120 175,120 200,150 C225,120 225,120 225,120 C250,90 275,75 300,75 C325,75 350,100 350,150 C350,250 200,350 200,350 Z"
                fill="rgba(255,255,255,0.2)"
                stroke="rgba(255,255,255,0.3)"
                strokeWidth="3"
              />
            </svg>
            {/* Cora√ß√£o m√©dio */}
            <svg
              width="320"
              height="320"
              viewBox="0 0 400 400"
              className="absolute top-10 left-10 opacity-25"
            >
              <path
                d="M200,350 C200,350 50,250 50,150 C50,100 75,75 100,75 C125,75 150,90 175,120 C175,120 175,120 200,150 C225,120 225,120 225,120 C250,90 275,75 300,75 C325,75 350,100 350,150 C350,250 200,350 200,350 Z"
                fill="rgba(255,255,255,0.15)"
                stroke="rgba(255,255,255,0.25)"
                strokeWidth="3"
              />
            </svg>
            {/* Cora√ß√£o pequeno interno */}
            <svg
              width="240"
              height="240"
              viewBox="0 0 400 400"
              className="absolute top-20 left-20 opacity-20"
            >
              <path
                d="M200,350 C200,350 50,250 50,150 C50,100 75,75 100,75 C125,75 150,90 175,120 C175,120 175,120 200,150 C225,120 225,120 225,120 C250,90 275,75 300,75 C325,75 350,100 350,150 C350,250 200,350 200,350 Z"
                fill="rgba(255,255,255,0.1)"
                stroke="rgba(255,255,255,0.2)"
                strokeWidth="2"
              />
            </svg>
            {/* Estrelinhas */}
            <div className="absolute top-16 right-20 text-white text-4xl opacity-60 animate-pulse">‚ú®</div>
            <div className="absolute top-32 right-12 text-white text-2xl opacity-40 animate-pulse delay-150">‚ú®</div>
          </div>
        </div>

        {/* Fotos dos usu√°rios */}
        <div className="relative z-10 mb-8 flex items-center justify-center">
          {/* Foto usu√°rio atual */}
          <div className="relative">
            <div className="w-32 h-32 rounded-full border-4 border-white shadow-2xl overflow-hidden bg-gray-200">
              {currentUserPhoto ? (
                <img
                  src={currentUserPhoto}
                  alt="Voc√™"
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-blue-400 to-purple-500 text-white text-4xl font-bold">
                  V
                </div>
              )}
            </div>
          </div>

          {/* Foto usu√°rio que deu match */}
          <div className="relative -ml-8">
            <div className="w-32 h-32 rounded-full border-4 border-white shadow-2xl overflow-hidden bg-gray-200">
              {matchedUserPhoto ? (
                <img
                  src={matchedUserPhoto}
                  alt={matchedUserName}
                  className="w-full h-full object-cover"
                />
              ) : (
                <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-pink-400 to-rose-500 text-white text-4xl font-bold">
                  {matchedUserName.charAt(0).toUpperCase()}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Texto "IT'S A Match" */}
        <div className="relative z-10 text-center mb-4">
          <h1 
            className="text-7xl font-black text-white mb-2"
            style={{
              textShadow: '4px 4px 8px rgba(0,0,0,0.3)',
              fontFamily: 'Impact, sans-serif',
              letterSpacing: '0.05em',
              WebkitTextStroke: '2px rgba(0,0,0,0.1)'
            }}
            data-testid="text-match-title"
          >
            IT'S A
          </h1>
          <h1 
            className="text-8xl font-black text-white italic"
            style={{
              textShadow: '4px 4px 8px rgba(0,0,0,0.3)',
              fontFamily: 'Impact, sans-serif',
              letterSpacing: '0.05em',
              WebkitTextStroke: '2px rgba(0,0,0,0.1)'
            }}
            data-testid="text-match"
          >
            Match
          </h1>
          <p className="text-white text-xl font-semibold mt-4" data-testid="text-matched-with">
            Voc√™ deu Match com {matchedUserName}
          </p>
        </div>

        {/* Campo de mensagem */}
        <div className="relative z-10 w-full max-w-sm mt-8">
          <div className="bg-gray-900/80 backdrop-blur-sm rounded-full p-2 flex items-center">
            <Input
              type="text"
              placeholder="Diga algo legal"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter' && !isSending) {
                  handleSendMessage(message);
                }
              }}
              className="flex-1 bg-transparent border-0 text-white placeholder:text-gray-400 focus-visible:ring-0 focus-visible:ring-offset-0 px-4"
              disabled={isSending}
              data-testid="input-first-message"
            />
            <button
              onClick={() => handleSendMessage(message)}
              disabled={!message.trim() || isSending}
              className="text-gray-400 hover:text-white px-4 font-semibold disabled:opacity-50 disabled:cursor-not-allowed"
              data-testid="button-send-message"
            >
              Enviar
            </button>
          </div>
        </div>

        {/* Emojis r√°pidos */}
        <div className="relative z-10 flex items-center justify-center gap-4 mt-6">
          {quickMessages.map((emoji, index) => (
            <button
              key={index}
              onClick={() => handleQuickMessage(emoji)}
              disabled={isSending}
              className="w-14 h-14 rounded-full border-2 border-white/40 bg-white/10 backdrop-blur-sm flex items-center justify-center text-3xl hover:bg-white/20 hover:scale-110 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              data-testid={`button-emoji-${index}`}
            >
              {emoji}
            </button>
          ))}
        </div>
      </div>

      {/* Bot√£o inferior */}
      <Button
        onClick={handleKeepSwiping}
        variant="outline"
        className="w-full max-w-sm bg-white/10 backdrop-blur-sm border-2 border-white text-white hover:bg-white/20 hover:text-white rounded-full h-12 font-semibold"
        data-testid="button-keep-swiping"
      >
        Continuar deslizando
      </Button>
    </div>
  );
}

