import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Copy, CheckCircle, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function OAuthDebug() {
  const [callbackUrl, setCallbackUrl] = useState("");
  const [copied, setCopied] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    // Get the current domain
    const domain = window.location.host;
    const protocol = window.location.protocol;
    const url = `${protocol}//${domain}/api/auth/google/callback`;
    setCallbackUrl(url);
  }, []);

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(callbackUrl);
      setCopied(true);
      toast({ title: "Copiado!", description: "URL de callback copiado para √°rea de transfer√™ncia" });
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      toast({ title: "Erro", description: "Falha ao copiar URL", variant: "destructive" });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 p-6">
      <div className="max-w-4xl mx-auto space-y-6 pt-8">
        <Card className="border-0 shadow-xl">
          <CardHeader>
            <CardTitle className="text-2xl">Configura√ß√£o do Google OAuth</CardTitle>
            <CardDescription>
              Configure o Google Cloud Console com o URL de callback correto
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                Para que o login com Google funcione, voc√™ precisa adicionar este URL nas configura√ß√µes do Google Cloud Console
              </AlertDescription>
            </Alert>

            <div className="space-y-2">
              <label className="text-sm font-medium">URL de Callback (Authorized redirect URIs)</label>
              <div className="flex gap-2">
                <div className="flex-1 p-3 bg-gray-100 rounded-lg font-mono text-sm break-all">
                  {callbackUrl}
                </div>
                <Button
                  onClick={copyToClipboard}
                  variant="outline"
                  size="icon"
                  className="shrink-0"
                  data-testid="button-copy-url"
                >
                  {copied ? <CheckCircle className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
                </Button>
              </div>
            </div>

            <div className="space-y-4 pt-4 border-t">
              <h3 className="font-semibold text-lg">Passo a Passo:</h3>

              <ol className="space-y-3 list-decimal list-inside">
                <li className="text-sm">
                  Acesse o <a 
                    href="https://console.cloud.google.com/apis/credentials" 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:underline"
                  >
                    Google Cloud Console - Credentials
                  </a>
                </li>

                <li className="text-sm">
                  Selecione suas credenciais OAuth 2.0 (OAuth client ID)
                </li>

                <li className="text-sm">
                  Em <strong>"Authorized redirect URIs"</strong>, clique em <strong>"ADD URI"</strong>
                </li>

                <li className="text-sm">
                  Cole o URL acima no campo
                </li>

                <li className="text-sm">
                  Clique em <strong>"SAVE"</strong>
                </li>

                <li className="text-sm">
                  Aguarde alguns minutos para as altera√ß√µes propagarem (pode levar at√© 5 minutos)
                </li>

                <li className="text-sm">
                  Tente fazer login com Google novamente
                </li>
              </ol>
            </div>

            <Alert className="bg-yellow-50 border-yellow-200">
              <AlertCircle className="h-4 w-4 text-yellow-600" />
              <AlertDescription className="text-yellow-800">
                <strong>Nota:</strong> Se voc√™ j√° tinha outro URL configurado, n√£o remova - adicione este como uma nova URI autorizada
              </AlertDescription>
            </Alert>

            <div className="flex gap-3 pt-4">
              <Button 
                onClick={() => window.location.href = '/login'}
                className="flex-1"
                data-testid="button-back-login"
              >
                Voltar para Login
              </Button>
              <Button 
                onClick={() => window.location.href = '/api/auth/google'}
                variant="outline"
                className="flex-1"
                data-testid="button-test-google"
              >
                Testar Google Login
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/match-profile.tsx.content:
import { useState } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { ArrowLeft, Share2, RotateCcw, Ban, Flag, Users } from "lucide-react";
import { VerifiedBadge } from "@/components/VerifiedBadge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { Profile } from "@shared/schema";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function MatchProfile() {
  const { id } = useParams();
  const [, setLocation] = useLocation();
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const { toast } = useToast();
  const [showUndoDialog, setShowUndoDialog] = useState(false);
  const [showBlockDialog, setShowBlockDialog] = useState(false);
  const [showReportDialog, setShowReportDialog] = useState(false);

  const { data: profile, isLoading } = useQuery<Profile>({
    queryKey: [`/api/profiles/${id}`],
  });

  const handlePhotoNavigation = (direction: 'prev' | 'next') => {
    if (!profile?.photos || profile.photos.length <= 1) return;

    if (direction === 'next') {
      setCurrentPhotoIndex((prev) => (prev + 1) % profile.photos.length);
    } else {
      setCurrentPhotoIndex((prev) => (prev - 1 + profile.photos.length) % profile.photos.length);
    }
  };

  // Mutation para desfazer match
  const undoMatchMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest(`/api/matches/${id}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      toast({
        title: "Match desfeito",
        description: "O match foi desfeito com sucesso.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/matches"] });
      setLocation("/matches");
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel desfazer o match.",
        variant: "destructive",
      });
    },
  });

  // Mutation para bloquear
  const blockMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest(`/api/block/${id}`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      toast({
        title: "Usu√°rio bloqueado",
        description: `${profile?.name} foi bloqueado com sucesso.`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/matches"] });
      setLocation("/matches");
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel bloquear este usu√°rio.",
        variant: "destructive",
      });
    },
  });

  // Mutation para denunciar
  const reportMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest(`/api/report/${id}`, {
        method: "POST",
        body: {
          reason: "Comportamento inapropriado",
        },
      });
    },
    onSuccess: () => {
      toast({
        title: "Den√∫ncia enviada",
        description: "Obrigado pela sua den√∫ncia. Vamos analis√°-la.",
      });
      setLocation("/matches");
    },
    onError: () => {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel enviar a den√∫ncia.",
        variant: "destructive",
      });
    },
  });

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: `Perfil de ${profile?.name}`,
          text: `Confira o perfil de ${profile?.name} no MIX!`,
          url: window.location.href,
        });
        toast({
          title: "Compartilhado!",
          description: "Perfil compartilhado com sucesso.",
        });
      } catch (error) {
        // Usu√°rio cancelou o compartilhamento
      }
    } else {
      // Copiar link para clipboard
      navigator.clipboard.writeText(window.location.href);
      toast({
        title: "Link copiado!",
        description: "Link do perfil copiado para a √°rea de transfer√™ncia.",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white">Carregando perfil...</p>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="h-screen flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900">
        <div className="text-center">
          <p className="text-white">Perfil n√£o encontrado</p>
          <Button
            onClick={() => setLocation("/matches")}
            className="mt-4 bg-white text-blue-900 hover:bg-gray-100"
          >
            Voltar
          </Button>
        </div>
      </div>
    );
  }

  const photos = profile.photos && profile.photos.length > 0 
    ? profile.photos 
    : [`https://ui-avatars.com/api/?name=${profile.name}&background=ec4899&color=fff&size=800`];

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-blue-900 via-blue-800 to-indigo-900 overflow-auto">
      {/* Header - Bot√£o voltar */}
      <div className="sticky top-0 z-50 p-4">
        <Button
          onClick={() => setLocation("/matches")}
          variant="ghost"
          size="icon"
          className="bg-white/10 hover:bg-white/20 text-white rounded-full backdrop-blur-sm"
          data-testid="button-back"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
      </div>

      {/* Foto do perfil */}
      <div className="px-6 mb-6">
        <div className="relative rounded-3xl overflow-hidden shadow-2xl">
          <img
            src={photos[currentPhotoIndex]}
            alt={profile.name}
            className="w-full aspect-[3/4] object-cover"
            data-testid="img-profile-photo"
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              target.onerror = null;
              target.src = `https://ui-avatars.com/api/?name=${profile.name}&background=ec4899&color=fff&size=800`;
            }}
          />

          {/* √Åreas clic√°veis para navega√ß√£o de fotos */}
          {photos.length > 1 && (
            <>
              <div 
                className="absolute left-0 top-0 bottom-0 w-1/2 cursor-pointer z-10"
                onClick={(e) => {
                  e.stopPropagation();
                  handlePhotoNavigation('prev');
                }}
                data-testid="area-prev-photo"
              />
              <div 
                className="absolute right-0 top-0 bottom-0 w-1/2 cursor-pointer z-10"
                onClick={(e) => {
                  e.stopPropagation();
                  handlePhotoNavigation('next');
                }}
                data-testid="area-next-photo"
              />
            </>
          )}

          {/* Barra de progresso de fotos */}
          {photos.length > 1 && (
            <div className="absolute top-2 left-2 right-2 z-20 flex gap-1.5">
              {photos.map((_, index) => (
                <div 
                  key={index} 
                  className="flex-1 h-1 bg-white/30 rounded-full overflow-hidden"
                  data-testid={`photo-indicator-${index}`}
                >
                  <div 
                    className={`h-full bg-white transition-all duration-300 ${
                      index === currentPhotoIndex ? 'w-full' : index < currentPhotoIndex ? 'w-full' : 'w-0'
                    }`}
                  />
                </div>
              ))}
            </div>
          )}

          {/* Informa√ß√µes sobrepostas na foto */}
          <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent p-6">
            <div className="flex items-center gap-3 mb-2">
              <h1 className="text-white text-3xl font-bold drop-shadow-lg" data-testid="text-profile-name">
                {profile.name}
              </h1>
              <span className="text-white text-2xl drop-shadow-lg">
                {profile.age}
              </span>
              {profile.isVerified && (
                <VerifiedBadge className="w-6 h-6 drop-shadow-lg" />
              )}
            </div>

            {profile.location && (
              <p className="text-white/90 text-sm drop-shadow-lg">
                üìç {profile.location}
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Bio */}
      {profile.bio && (
        <div className="px-6 mb-4">
          <div className="bg-white/5 backdrop-blur-sm rounded-2xl p-6">
            <h3 className="text-white/70 text-sm font-semibold mb-3">Sobre mim</h3>
            <p className="text-white text-base leading-relaxed" data-testid="text-bio">
              {profile.bio}
            </p>
          </div>
        </div>
      )}

      {/* Interesses */}
      {profile.interests && profile.interests.length > 0 && (
        <div className="px-6 mb-4">
          <div className="bg-white/5 backdrop-blur-sm rounded-2xl p-6">
            <h3 className="text-white/70 text-sm font-semibold mb-3">Interesses</h3>
            <div className="flex flex-wrap gap-2">
              {profile.interests.map((interest, index) => {
                const colors = [
                  'bg-gradient-to-r from-pink-500 to-rose-500',
                  'bg-gradient-to-r from-purple-500 to-indigo-500',
                  'bg-gradient-to-r from-blue-500 to-cyan-500',
                  'bg-gradient-to-r from-green-500 to-emerald-500',
                  'bg-gradient-to-r from-orange-500 to-amber-500',
                  'bg-gradient-to-r from-violet-500 to-purple-500',
                ];
                const colorClass = colors[index % colors.length];

                return (
                  <span 
                    key={index}
                    className={`px-4 py-2 ${colorClass} text-white rounded-full text-sm font-medium shadow-lg`}
                    data-testid={`interest-${index}`}
                  >
                    {interest}
                  </span>
                );
              })}
            </div>
          </div>
        </div>
      )}

      {/* Se√ß√£o Amigos em Comum */}
      <div className="px-6 mb-4">
        <div className="bg-white/5 backdrop-blur-sm rounded-2xl p-6">
          <div className="flex items-center gap-3 mb-2">
            <Users className="w-5 h-5 text-white/70" />
            <h3 className="text-white/70 text-sm font-semibold">Amigos em comum</h3>
          </div>
          <p className="text-white text-base">Usando o Amigos em comum</p>
        </div>
      </div>

      {/* Bot√£o: Compartilhar perfil */}
      <div className="px-6 mb-3">
        <button
          onClick={handleShare}
          className="w-full bg-white/5 backdrop-blur-sm rounded-2xl py-5 text-center text-white text-lg font-medium hover:bg-white/10 transition-all"
          data-testid="button-share"
        >
          Compartilhar perfil de {profile.name}
        </button>
      </div>

      {/* Bot√£o: Desfazer match */}
      <div className="px-6 mb-3">
        <button
          onClick={() => setShowUndoDialog(true)}
          className="w-full bg-white/5 backdrop-blur-sm rounded-2xl py-5 text-center text-white text-lg font-medium hover:bg-white/10 transition-all"
          data-testid="button-undo-match"
        >
          Desfazer match
        </button>
      </div>

      {/* Bot√£o: Bloquear */}
      <div className="px-6 mb-3">
        <button
          onClick={() => setShowBlockDialog(true)}
          className="w-full bg-white/5 backdrop-blur-sm rounded-2xl py-5 text-center text-white text-lg font-medium hover:bg-white/10 transition-all"
          data-testid="button-block"
        >
          Bloquear {profile.name}
        </button>
      </div>

      {/* Bot√£o: Denunciar */}
      <div className="px-6 mb-8">
        <button
          onClick={() => setShowReportDialog(true)}
          className="w-full bg-white/5 backdrop-blur-sm rounded-2xl py-5 text-center text-red-400 text-lg font-medium hover:bg-white/10 transition-all"
          data-testid="button-report"
        >
          Denunciar {profile.name}
        </button>
      </div>

      {/* Dialog: Desfazer Match */}
      <AlertDialog open={showUndoDialog} onOpenChange={setShowUndoDialog}>
        <AlertDialogContent className="bg-gray-900 text-white border-gray-800">
          <AlertDialogHeader>
            <AlertDialogTitle>Desfazer match?</AlertDialogTitle>
            <AlertDialogDescription className="text-gray-400">
              Voc√™ tem certeza que deseja desfazer o match com {profile.name}? Esta a√ß√£o n√£o pode ser desfeita.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel className="bg-gray-800 text-white hover:bg-gray-700 border-0">
              Cancelar
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                undoMatchMutation.mutate();
                setShowUndoDialog(false);
              }}
              className="bg-blue-600 hover:bg-blue-700"
              disabled={undoMatchMutation.isPending}
            >
              {undoMatchMutation.isPending ? "Desfazendo..." : "Desfazer"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Dialog: Bloquear */}
      <AlertDialog open={showBlockDialog} onOpenChange={setShowBlockDialog}>
        <AlertDialogContent className="bg-gray-900 text-white border-gray-800">
          <AlertDialogHeader>
            <AlertDialogTitle>Bloquear {profile.name}?</AlertDialogTitle>
            <AlertDialogDescription className="text-gray-400">
              Voc√™ n√£o ver√° mais o perfil desta pessoa e ela n√£o poder√° ver o seu. Esta a√ß√£o pode ser desfeita nas configura√ß√µes.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel className="bg-gray-800 text-white hover:bg-gray-700 border-0">
              Cancelar
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                blockMutation.mutate();
                setShowBlockDialog(false);
              }}
              className="bg-red-600 hover:bg-red-700"
              disabled={blockMutation.isPending}
            >
              {blockMutation.isPending ? "Bloqueando..." : "Bloquear"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Dialog: Denunciar */}
      <AlertDialog open={showReportDialog} onOpenChange={setShowReportDialog}>
        <AlertDialogContent className="bg-gray-900 text-white border-gray-800">
          <AlertDialogHeader>
            <AlertDialogTitle>Denunciar {profile.name}?</AlertDialogTitle>
            <AlertDialogDescription className="text-gray-400">
              Sua den√∫ncia √© an√¥nima. Analisaremos o perfil e tomaremos as medidas necess√°rias.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel className="bg-gray-800 text-white hover:bg-gray-700 border-0">
              Cancelar
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                reportMutation.mutate();
                setShowReportDialog(false);
              }}
              className="bg-red-600 hover:bg-red-700"
              disabled={reportMutation.isPending}
            >
              {reportMutation.isPending ? "Denunciando..." : "Denunciar"}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/match-celebration.tsx.content:
import { useState } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { Profile } from "@shared/schema";

export default function MatchCelebration() {
  const { matchId } = useParams();
  const [, setLocation] = useLocation();
  const [message, setMessage] = useState("");
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const { toast } = useToast();

  const { data: match, isLoading } = useQuery({
    queryKey: [`/api/match/${matchId}`],
    queryFn: async () => {
      const res = await fetch(`/api/match/${matchId}`, {
        credentials: 'include',
      });
      if (!res.ok) throw new Error('Failed to fetch match');
      return res.json();
    },
    enabled: !!matchId,
  });

  const { data: currentUser } = useQuery({
    queryKey: ['/api/auth/user'],
    queryFn: async () => {
      const res = await fetch('/api/auth/user', { credentials: 'include' });
      if (!res.ok) throw new Error('Not authenticated');
      return res.json();
    },
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest(`/api/matches/${matchId}/messages`, {
        method: 'POST',
        body: JSON.stringify({ content }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/matches/${matchId}/messages`] });
      queryClient.invalidateQueries({ queryKey: ['/api/matches'] });
      toast({
        title: "Mensagem enviada!",
        description: "Comece a conversa agora",
      });
      setLocation(`/chat/${matchId}`);
    },
    onError: () => {
      toast({
        title: "Erro ao enviar",
        description: "Tente novamente",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = async () => {
    if (!message.trim()) return;
    sendMessageMutation.mutate(message);
  };

  const handleEmojiClick = (emoji: string) => {
    setMessage(prev => prev + emoji);
  };

  const handleClose = () => {
    setLocation("/matches");
  };

  if (isLoading || !match || !currentUser) {
    return (
      <div className="h-screen flex items-center justify-center bg-gradient-to-br from-green-400 via-green-500 to-green-600">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white">Carregando...</p>
        </div>
      </div>
    );
  }

  const otherProfile = match.profile as Profile;

  // Get all photos from both users
  const currentUserPhotos = currentUser.photos && currentUser.photos.length > 0 
    ? currentUser.photos 
    : [currentUser.profileImage || `https://ui-avatars.com/api/?name=${currentUser.firstName}&background=ec4899&color=fff&size=200`];

  const otherUserPhotos = otherProfile.photos && otherProfile.photos.length > 0
    ? otherProfile.photos
    : [`https://ui-avatars.com/api/?name=${otherProfile.name}&background=ec4899&color=fff&size=200`];

  const currentUserPhoto = currentUserPhotos[currentPhotoIndex] || currentUserPhotos[0];
  const otherUserPhoto = otherUserPhotos[currentPhotoIndex] || otherUserPhotos[0];
  const totalPhotos = Math.max(currentUserPhotos.length, otherUserPhotos.length);

  const handlePhotoClick = (direction: 'left' | 'right') => {
    if (direction === 'left' && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(prev => prev - 1);
    } else if (direction === 'right' && currentPhotoIndex < totalPhotos - 1) {
      setCurrentPhotoIndex(prev => prev + 1);
    }
  };

  return (
    <div className="h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-600 flex flex-col relative overflow-hidden">
      {/* Bot√£o Fechar */}
      <button
        onClick={handleClose}
        className="absolute top-6 left-6 z-50 w-10 h-10 bg-transparent text-white hover:bg-white/10 rounded-full flex items-center justify-center transition-colors"
        data-testid="button-close"
      >
        <X className="w-6 h-6" />
      </button>

      {/* Efeitos de fundo - cora√ß√µes flutuantes */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Cora√ß√µes grandes com blur */}
        <div className="absolute top-20 left-10 w-32 h-32 bg-white/10 rounded-full blur-3xl animate-pulse" />
        <div className="absolute top-40 right-20 w-40 h-40 bg-white/10 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '1s' }} />
        <div className="absolute bottom-32 left-1/4 w-36 h-36 bg-white/10 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '2s' }} />
        <div className="absolute bottom-20 right-1/3 w-44 h-44 bg-white/10 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '1.5s' }} />

        {/* Estrelas pequenas */}
        <div className="absolute top-32 right-10 text-white text-3xl animate-pulse">‚ú®</div>
        <div className="absolute top-48 left-20 text-white text-2xl animate-pulse" style={{ animationDelay: '0.5s' }}>‚ú®</div>
        <div className="absolute top-24 left-1/2 text-white text-4xl animate-pulse" style={{ animationDelay: '1s' }}>‚ú®</div>
      </div>

      {/* Conte√∫do Principal */}
      <div className="flex-1 flex flex-col items-center justify-center px-6 relative z-10">
        {/* Fotos Sobrepostas com Navega√ß√£o */}
        <div className="relative mb-8">
          {/* √Årea de navega√ß√£o invis√≠vel - lado esquerdo */}
          {currentPhotoIndex > 0 && (
            <button
              onClick={() => handlePhotoClick('left')}
              className="absolute left-0 top-0 w-20 h-32 z-20 cursor-pointer"
              aria-label="Foto anterior"
            />
          )}

          {/* √Årea de navega√ß√£o invis√≠vel - lado direito */}
          {currentPhotoIndex < totalPhotos - 1 && (
            <button
              onClick={() => handlePhotoClick('right')}
              className="absolute right-0 top-0 w-32 h-32 z-20 cursor-pointer"
              aria-label="Pr√≥xima foto"
            />
          )}

          {/* Foto do usu√°rio atual (esquerda) */}
          <div className="relative w-32 h-32 rounded-full border-4 border-white overflow-hidden shadow-2xl">
            <img
              src={currentUserPhoto}
              alt="Voc√™"
              className="w-full h-full object-cover transition-all duration-300"
              data-testid="img-current-user"
              onError={(e) => {
                console.error("üî¥ Erro ao carregar foto do usu√°rio atual");
                const target = e.target as HTMLImageElement;
                target.onerror = null; // ‚úÖ Evitar loop infinito
                target.src = `https://ui-avatars.com/api/?name=${currentUser.firstName}&background=ec4899&color=fff&size=200`;
              }}
            />

            {/* Indicador de fotos - usu√°rio atual */}
            {currentUserPhotos.length > 1 && (
              <div className="absolute top-2 left-0 right-0 flex gap-1 px-2 justify-center">
                {currentUserPhotos.map((_: string, idx: number) => (
                  <div
                    key={idx}
                    className={`h-0.5 flex-1 rounded-full transition-all ${
                      idx === currentPhotoIndex 
                        ? 'bg-white' 
                        : idx < currentPhotoIndex 
                          ? 'bg-white/80' 
                          : 'bg-white/30'
                    }`}
                  />
                ))}
              </div>
            )}
          </div>

          {/* Foto do match (direita, sobreposta) */}
          <div className="absolute -right-24 top-0 w-32 h-32 rounded-full border-4 border-white overflow-hidden shadow-2xl">
            <img
              src={otherUserPhoto}
              alt={otherProfile.name}
              className="w-full h-full object-cover transition-all duration-300"
              data-testid="img-match-user"
              onError={(e) => {
                console.error("üî¥ Erro ao carregar foto do match");
                const target = e.target as HTMLImageElement;
                target.onerror = null; // ‚úÖ Evitar loop infinito
                target.src = `https://ui-avatars.com/api/?name=${otherProfile.name}&background=ec4899&color=fff&size=200`;
              }}
            />

            {/* Indicador de fotos - outro usu√°rio */}
            {otherUserPhotos.length > 1 && (
              <div className="absolute top-2 left-0 right-0 flex gap-1 px-2 justify-center">
                {otherUserPhotos.map((_: string, idx: number) => (
                  <div
                    key={idx}
                    className={`h-0.5 flex-1 rounded-full transition-all ${
                      idx === currentPhotoIndex 
                        ? 'bg-white' 
                        : idx < currentPhotoIndex 
                          ? 'bg-white/80' 
                          : 'bg-white/30'
                    }`}
                  />
                ))}
              </div>
            )}
          </div>

          {/* Contador de fotos */}
          {totalPhotos > 1 && (
            <div className="absolute -bottom-6 left-1/2 -translate-x-1/2 bg-black/50 text-white text-xs px-3 py-1 rounded-full backdrop-blur-sm">
              {currentPhotoIndex + 1} / {totalPhotos}
            </div>
          )}
        </div>

        {/* Texto IT'S A MATCH */}
        <div className="text-center mb-6 mt-16">
          <h1 className="text-white text-6xl font-black mb-2 drop-shadow-2xl" style={{ 
            fontFamily: 'system-ui, -apple-system, sans-serif',
            fontStyle: 'italic',
            letterSpacing: '-0.02em'
          }}>
            IT'S A
          </h1>
          <h2 className="text-white text-7xl font-black drop-shadow-2xl" style={{ 
            fontFamily: 'system-ui, -apple-system, sans-serif',
            fontStyle: 'italic',
            letterSpacing: '-0.02em'
          }}>
            Match
          </h2>
        </div>

        {/* Nome do match */}
        <p className="text-white text-lg font-medium mb-8 drop-shadow-lg">
          Voc√™ deu Match com {otherProfile.name}
        </p>

        {/* Campo de mensagem */}
        <div className="w-full max-w-md bg-gray-900 rounded-full px-6 py-4 shadow-2xl">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            placeholder="Diga algo legal"
            className="w-full bg-transparent text-white placeholder-gray-400 outline-none text-base"
            data-testid="input-message"
          />
          <button
            onClick={handleSendMessage}
            disabled={!message.trim() || sendMessageMutation.isPending}
            className="absolute right-8 text-blue-400 hover:text-blue-300 font-semibold disabled:opacity-50 disabled:cursor-not-allowed"
            data-testid="button-send"
          >
            Enviar
          </button>
        </div>

        {/* Emojis */}
        <div className="flex gap-4 mt-6">
          {['üëã', 'üòä', '‚ù§Ô∏è', 'üòç'].map((emoji) => (
            <button
              key={emoji}
              onClick={() => handleEmojiClick(emoji)}
              className="w-14 h-14 bg-gray-800/50 hover:bg-gray-800 backdrop-blur-sm rounded-full flex items-center justify-center text-2xl transition-all hover:scale-110"
              data-testid={`emoji-${emoji}`}
            >
              {emoji}
            </button>
          ))}
        </div>
      </div>

      {/* Bot√£o "Continuar Deslizando" */}
      <div className="pb-8 px-6">
        <Button
          onClick={() => setLocation("/discover")}
          variant="outline"
          className="w-full max-w-md mx-auto block bg-transparent border-2 border-white text-white hover:bg-white hover:text-green-600 py-6 text-lg font-semibold rounded-full"
          data-testid="button-keep-swiping"
        >
          Continuar Deslizando
        </Button>
      </div>
    </div>
  );
}

server/migrate-production.ts.content:
import { db } from './db';
import { sql } from 'drizzle-orm';

/**
 * Migration autom√°tica para produ√ß√£o
 * Executa automaticamente quando o app inicia em produ√ß√£o
 */
export async function migrateProduction() {
  if (process.env.NODE_ENV !== 'production') {
    console.log('‚è≠Ô∏è  Skipping production migration (not in production mode)');
    return;
  }

  console.log('üîÑ Running production database migration...');

  try {
    // 1. Verificar se coluna phone existe
    const phoneExists = await db.execute(sql`
      SELECT EXISTS (
        SELECT 1 FROM information_schema.columns 
        WHERE table_name = 'users' AND column_name = 'phone'
      ) as exists
    `);

    const hasPhone = (phoneExists.rows[0] as any)?.exists;

    if (!hasPhone) {
      console.log('üìù Adding phone column to users table...');
      await db.execute(sql`ALTER TABLE users ADD COLUMN phone TEXT`);
      console.log('‚úÖ Phone column added');
    } else {
      console.log('‚úÖ Phone column already exists');
    }

    // 2. Criar √≠ndice √∫nico para phone
    console.log('üìù Creating unique index for phone...');
    await db.execute(sql`
      CREATE UNIQUE INDEX IF NOT EXISTS users_phone_unique 
      ON users(phone) WHERE phone IS NOT NULL AND phone != ''
    `);
    console.log('‚úÖ Phone index created');

    // 3. Verificar todas as colunas necess√°rias
    const requiredColumns = [
      'birth_date',
      'gender',
      'sexual_orientation',
      'interested_in',
      'city',
      'location',
      'education',
      'company',
      'school',
      'interests',
      'bio',
      'photos'
    ];

    for (const column of requiredColumns) {
      const exists = await db.execute(sql.raw(`
        SELECT EXISTS (
          SELECT 1 FROM information_schema.columns 
          WHERE table_name = 'users' AND column_name = '${column}'
        ) as exists
      `));

      const hasColumn = (exists.rows[0] as any)?.exists;

      if (!hasColumn) {
        console.log(`üìù Adding column: ${column}...`);

        // Determinar o tipo da coluna
        if (['sexual_orientation', 'interested_in', 'interests', 'photos'].includes(column)) {
          await db.execute(sql.raw(`ALTER TABLE users ADD COLUMN ${column} TEXT[] DEFAULT '{}'`));
        } else {
          await db.execute(sql.raw(`ALTER TABLE users ADD COLUMN ${column} TEXT`));
        }

        console.log(`‚úÖ Column ${column} added`);
      }
    }

    console.log('üéâ Production migration completed successfully!');
  } catch (error) {
    console.error('‚ùå Production migration failed:', error);
    // N√£o bloquear o app se a migra√ß√£o falhar
    console.error('‚ö†Ô∏è  App will continue, but database may need manual migration');
  }
}

