  company?: string;
  school?: string;
  height?: number;
  relationshipGoals?: string;
  languages?: string[];
  starSign?: string;
  educationLevel?: string;
  familyPlans?: string;
  personalityType?: string;
  communicationStyle?: string;
  loveStyle?: string;
  instagram?: string;
  spotify?: string;
}

export default function Profile() {
