        console.log("üîµ SALVANDO ONBOARDING COMPLETO");
        console.log("üîµ ========================================");
        console.log("üîµ Birthday formato original:", birthday);

        // Converter data de DD/MM/YYYY para YYYY-MM-DD
        let formattedBirthDate = birthday;
        if (birthday && birthday.includes('/')) {
